data.schema:
function({_req}) {
    const type = _req.query.type;
    if (type === 'icon') {
      return {
        icon_status: true,
        icon_title: '红楼梦',
        icon_desc: '一个是阆苑仙葩，一个是美玉无瑕。',
        icon_src: 'https://songshuzhong.github.io/i-assets/public/img/h5/hongloumeng.jpeg'

      }
    }
    if (type === 'home') {
      return {
        home_status: false,
        home_title: '三国演义',
        home_desc: '暗淡了刀光剑影，远去了鼓角争鸣。',
        home_src: 'https://songshuzhong.github.io/i-assets/public/img/h5/sanguoyanyi.jpeg'
      }
    }
    if (type === 'bubble') {
      return {
        bubble_status: true,
        bubble_title: '水浒传',
        bubble_desc: '天南地北。问乾坤何处，可容狂客。',
        bubble_src: 'https://songshuzhong.github.io/i-assets/public/img/h5/shuihuzhuan.png'
      }
    }
    if (type === 'thumb') {
      return {
        thumb_status: true,
        thumb_title: '西游记',
        thumb_desc: '鸿蒙初辟本无性，打破冥顽须悟空。',
        thumb_src: 'https://songshuzhong.github.io/i-assets/public/img/h5/xiyouji.jpeg'
      }
    }
  }
