data.pageSchema:
{
  "renderer": "page",
  "initData": {
    "fertilizer": 70,
    "medicine": 36,
    "agriculture": 91
  },
  "body": [
    {
      "renderer": "wrapper",
      "classname": "monitor-header__wrapper move_1",
      "body": [
        {
          "renderer": "html",
          "classname": "font-size-14 color-primary",
          "html": "2022-08-24 16:46:19 星期三 第三季度"
        },
        {
          "renderer": "html",
          "classname": "monitor-header",
          "html": "智慧农业系统平台"
        },
        {
          "renderer": "form",
          "controls": [
            {
              "renderer": "datepicker",
              "name": "start_time",
              "label": "开始时间",
              "defaultValue": "",
              "tip": "",
              "type": "date",
              "readonly": false,
              "editable": true,
              "clearable": true,
              "placeholder": "",
              "startPlaceholder": "",
              "endPlaceholder": "",
              "format": "YYYY-MM-DD"
            },
            {
              "renderer": "action",
              "text": "搜索",
              "actionType": "submit",
              "actionApi": {
                "url": "",
                "method": "post",
                "params": {}
              },
              "params": {},
              "body": {}
            }
          ],
          "actionApi": {},
          "submitType": "target",
          "target": "ChartService",
          "inline": true,
          "labelWidth": 0
        }
      ],
      "bodyClassname": "i-mobile-flex-column"
    },
    {
      "renderer": "grid",
      "span": 8,
      "gutter": 4,
      "body": [
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "table",
              "initApi": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/wether"
              },
              "border": false,
              "hasPageInfo": false,
              "height": 220,
              "columns": [
                {
                  "name": "time",
                  "label": "时间"
                },
                {
                  "name": "wether",
                  "label": "天气"
                },
                {
                  "name": "temperature",
                  "label": "温度"
                },
                {
                  "name": "rain",
                  "label": "降水"
                },
                {
                  "name": "wind",
                  "label": "风"
                }
              ]
            },
            {
              "renderer": "card",
              "classname": "monitor-card",
              "body": [
                {
                  "renderer": "avatar",
                  "icon": "Iphone",
                  "description": "99",
                  "subTitle": "园区设备",
                  "size": 39,
                  "title": "",
                  "shape": "circle",
                  "borderRadius": 8,
                  "invert": true,
                  "color": "#22BC76",
                  "bgColor": "#FFFFFF"
                },
                {
                  "renderer": "avatar",
                  "icon": "Monitor",
                  "description": "19",
                  "subTitle": "预警设备",
                  "size": 39,
                  "title": "",
                  "shape": "circle",
                  "borderRadius": 8,
                  "invert": true,
                  "color": "#E2356D",
                  "bgColor": "#ffffff"
                },
                {
                  "renderer": "avatar",
                  "icon": "VideoCameraFilled",
                  "description": "20",
                  "subTitle": "运行设备",
                  "size": 39,
                  "title": "",
                  "shape": "circle",
                  "borderRadius": 8,
                  "invert": true,
                  "color": "#43BBEF",
                  "bgColor": "#ffffff"
                }
              ],
              "footer": [
                {
                  "renderer": "wrapper",
                  "classname": "monitor-online flex",
                  "body": [
                    {
                      "renderer": "html",
                      "html": "阳光玫瑰种植|126天"
                    },
                    {
                      "renderer": "html",
                      "html": "设备在线"
                    }
                  ]
                }
              ],
              "shadow": "never"
            }
          ],
          "classname": "move_4"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "service",
              "name": "ChartService",
              "initApi": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/wordcloud"
              },
              "body": [
                {
                  "renderer": "chart",
                  "useWordCloud": true,
                  "height": 385,
                  "config": {
                    "grid": {
                      "top": 15,
                      "right": 15,
                      "bottom": 20,
                      "left": 30
                    },
                    "series": [
                      {
                        "type": "wordCloud",
                        "sizeRange": [
                          12,
                          40
                        ],
                        "rotationRange": [
                          0,
                          0
                        ],
                        "rotationStep": 45,
                        "gridSize": 20,
                        "shape": "circle",
                        "width": "100%",
                        "height": "100%",
                        "data": "${wordcloud}"
                      }
                    ]
                  }
                }
              ]
            }
          ],
          "classname": "move_3"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "chart",
              "name": "ChartService",
              "height": 240,
              "config": {
                "grid": {
                  "top": 15,
                  "right": 15,
                  "bottom": 20,
                  "left": 30
                },
                "tooltip": {
                  "trigger": "axis"
                },
                "xAxis": {
                  "type": "category",
                  "boundaryGap": false,
                  "data": [
                    "1天",
                    "2天",
                    "3天",
                    "4天",
                    "5天",
                    "6天",
                    "7天"
                  ]
                },
                "yAxis": {
                  "type": "value"
                },
                "series": [
                  {
                    "name": "邮件营销",
                    "type": "line",
                    "stack": "总量",
                    "data": [
                      12,
                      32,
                      11,
                      34,
                      90,
                      23,
                      21
                    ]
                  },
                  {
                    "name": "联盟广告",
                    "type": "line",
                    "stack": "总量",
                    "data": [
                      22,
                      82,
                      91,
                      24,
                      90,
                      30,
                      30
                    ]
                  },
                  {
                    "name": "视频广告",
                    "type": "line",
                    "stack": "总量",
                    "data": [
                      50,
                      32,
                      18,
                      14,
                      90,
                      30,
                      50
                    ]
                  }
                ]
              }
            },
            {
              "renderer": "divider"
            },
            {
              "renderer": "progress",
              "exp": "data.fertilizer",
              "strokeWidth": 18,
              "textInside": true,
              "text": "施肥率"
            },
            {
              "renderer": "progress",
              "exp": "data.medicine",
              "strokeWidth": 18,
              "textInside": true,
              "text": "施药率"
            },
            {
              "renderer": "progress",
              "exp": "data.agriculture",
              "strokeWidth": 18,
              "textInside": true,
              "text": "农事率"
            }
          ],
          "classname": "move_2"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "service",
              "name": "ChartService",
              "initApi": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/data/pie"
              },
              "body": [
                {
                  "renderer": "chart",
                  "config": {
                    "grid": {
                      "top": 50,
                      "right": 20,
                      "bottom": 30,
                      "left": 30
                    },
                    "tooltip": {
                      "trigger": "item"
                    },
                    "series": [
                      {
                        "name": "面积模式",
                        "type": "pie",
                        "radius": [
                          20,
                          50
                        ],
                        "center": [
                          "50%",
                          "50%"
                        ],
                        "roseType": "area",
                        "itemStyle": {
                          "borderRadius": 8
                        },
                        "data": "${pie}"
                      }
                    ]
                  }
                }
              ]
            }
          ],
          "classname": "move_4"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "service",
              "name": "ChartService",
              "initApi": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/bar"
              },
              "body": [
                {
                  "renderer": "chart",
                  "config": {
                    "grid": {
                      "top": 15,
                      "right": 15,
                      "bottom": 20,
                      "left": 30
                    },
                    "tooltip": {
                      "trigger": "axis"
                    },
                    "xAxis": {
                      "type": "category",
                      "boundaryGap": false,
                      "data": [
                        "02:00",
                        "04:00",
                        "06:00",
                        "08:00",
                        "10:00",
                        "12:00",
                        "14:00"
                      ]
                    },
                    "yAxis": {
                      "type": "value"
                    },
                    "series": [
                      {
                        "itemStyle": {
                          "color": "#289df5",
                          "borderColor": "#289df5",
                          "areaStyle": {
                            "type": "default",
                            "opacity": 0.1
                          }
                        },
                        "data": "${bar}",
                        "type": "line",
                        "areaStyle": {}
                      }
                    ]
                  }
                }
              ]
            }
          ],
          "classname": "move_3"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "service",
              "name": "ChartService",
              "initApi": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/bar"
              },
              "body": [
                {
                  "renderer": "chart",
                  "config": {
                    "grid": {
                      "top": 15,
                      "right": 15,
                      "bottom": 20,
                      "left": 30
                    },
                    "tooltip": {
                      "trigger": "axis"
                    },
                    "xAxis": {
                      "type": "category",
                      "data": [
                        "1天",
                        "2天",
                        "3天",
                        "4天",
                        "5天",
                        "6天",
                        "7天"
                      ]
                    },
                    "yAxis": {
                      "type": "value"
                    },
                    "series": [
                      {
                        "data": "${bar}",
                        "type": "bar"
                      }
                    ]
                  }
                }
              ]
            }
          ],
          "classname": "move_2"
        }
      ]
    }
  ],
  "steps": [],
  "mergeUrlQuery": true,
  "showBacktop": true,
  "innerStyle": ".monitor-header__wrapper {\n  .i-wrapper__container__body {\n    width: 100%;\n    display: flex;\n    justify-content: space-between;\n  }\n}\n.monitor-header {\n    margin-bottom: 20px;\n    text-align: center;\n    font-size: 20px;\n    color: var(--el-color-primary);\n    -webkit-box-reflect:below 0 -webkit-linear-gradient(transparent,transparent 50%,rgba(255,255,255,.3));\n}\n.monitor-card {\n    border: 0;\n    .i-card__body { \n        display: flex; \n        justify-content: space-between;\n    }\n}\n.monitor-online {\n    position: relative;\n    border: 1px solid var(--el-color-primary);\n    width: 100%;\n    border-radius: 35px;\n    padding: 5px 20px 5px 40px;\n    margin-top: 15px;\n    color: var(--el-color-primary);\n    &::before {\n        position: absolute;\n        left: 5px;\n        top: 3px;\n        width: 25px;\n        height: 25px;\n        border: 1px solid var(--el-color-primary);\n        border-radius: 100%;\n        content: '';\n    }\n    .i-wrapper__container__body {\n        display: flex;\n        align-items: center;\n        justify-content: space-between;\n    }\n}\n.el-progress+.el-progress {\n    margin-top: 21px;\n}"
}
