pageSchema:
{
  "renderer": "page",
  "initData": {
    "name": "frfafad",
    "syncValid": 123,
    "sex": "0",
    "role": "1234",
    "connect": "0",
    "mail": "fasdgre@jl.com",
    "phone": "4665432465",
    "createdAt": "",
    "adult": true,
    "color": "#456533",
    "rate": 5,
    "switch_check": true,
    "textarea": "234",
    "richtxt": "eee",
    "asyncValid": "",
    "image": "https://songshuzhong.github.io/i-assets/public/img/shuttle.png",
    "files": {
      "src": "https: //codeload.github.com/songshuzhong/i-renderer-sample/zip/refs/heads/master",
      "name": "i-renderer-sample.zip"
    },
    "video": {
      "cover": "https://songshuzhong.github.io/i-assets/public/ironMan.jpeg",
      "src": "https://songshuzhong.github.io/i-assets/public/IronMan.mp4"
    },
    "manual": {
      "name": "ironMan.mp4",
      "cover": "https://songshuzhong.github.io/i-assets/public/ironMan.jpeg",
      "src": "https://songshuzhong.github.io/i-assets/public/IronMan.mp4"
    },
    "calendar": "",
    "richtext": "",
    "schedule_date": {
      "4": [
        0
      ]
    }
  },
  "body": [
    {
      "renderer": "form",
      "disabledOn": "data.isFormDisabled",
      "actionApi": {
        "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/form/submit",
        "params": {
          "*": "*"
        },
        "method": "post"
      },
      "controls": [
        {
          "renderer": "divider",
          "text": "基础控件"
        },
        {
          "renderer": "input",
          "name": "name",
          "label": "姓名",
          "tip": "请填写姓名",
          "placeholder": "请填写姓名",
          "requiredOn": true,
          "type": "text",
          "validType": "normal"
        },
        {
          "renderer": "switch",
          "defaultValue": false,
          "confirmTitle": "确定吗？",
          "rules": [],
          "label": "开 关",
          "name": "switch1"
        },
        {
          "renderer": "switch",
          "name": "switch_check",
          "label": "开关选择框",
          "type": "checkbox"
        },
        {
          "renderer": "switch",
          "name": "switch_button",
          "label": "开关按钮",
          "type": "button",
          "defaultValue": false,
          "activeText": "开 启",
          "inactiveText": "关 闭",
          "rules": []
        },
        {
          "renderer": "input",
          "name": "asyncValid",
          "label": "异步校验",
          "tip": "异步校验",
          "placeholder": "异步校验",
          "type": "number",
          "validType": "async",
          "rules": [
            {
              "exp": "/api/mock/form/verify?asyncValid=${asyncValid}",
              "type": "api",
              "trigger": "blur"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "sex",
          "label": "性别",
          "tip": "性别",
          "defaultValue": "0",
          "options": [
            {
              "value": "1",
              "text": "男"
            },
            {
              "value": "0",
              "text": "女"
            }
          ]
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 1",
          "options": [
            {
              "value": "1",
              "text": "爷爷"
            },
            {
              "value": "2",
              "text": "大伯"
            },
            {
              "value": "3",
              "text": "叔叔"
            },
            {
              "value": "4",
              "text": "哥哥"
            },
            {
              "value": "5",
              "text": "弟弟"
            }
          ]
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 0",
          "options": [
            {
              "value": "6",
              "text": "奶奶"
            },
            {
              "value": "7",
              "text": "大姑"
            },
            {
              "value": "8",
              "text": "小姑"
            },
            {
              "value": "9",
              "text": "姐姐"
            },
            {
              "value": "0",
              "text": "妹妹"
            }
          ]
        },
        {
          "renderer": "cascader",
          "name": "cascader",
          "label": "级联",
          "initApi": {
            "url": "/api/mock/cities",
            "method": "get"
          },
          "placeholder": "请输入",
          "isPanel": true,
          "rules": [],
          "multiple": true
        },
        {
          "renderer": "radio",
          "name": "connect",
          "label": "联系方式",
          "tip": "联系方式",
          "defaultValue": "0",
          "options": [
            {
              "value": "0",
              "text": "电话"
            },
            {
              "value": "1",
              "text": "邮箱"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "mail",
          "label": "邮件",
          "tip": "填写邮箱",
          "visibleOn": "data.connect == 1",
          "placeholder": "请填写邮箱"
        },
        {
          "renderer": "input",
          "name": "phone",
          "label": "电话",
          "tip": "预留电话",
          "visibleOn": "data.connect == 0",
          "placeholder": "请填写电话"
        },
        {
          "renderer": "datepicker",
          "name": "createdAt",
          "tip": "请选择派送日期",
          "type": "datetime",
          "readonly": false,
          "disabledOn": "data.name=='shuzhong'",
          "label": "日期选择器3",
          "format": "YYYY-MM-DD",
          "rules": []
        },
        {
          "renderer": "switch",
          "name": "adult",
          "label": "成年",
          "defaultValue": true,
          "tip": "已满十八岁"
        },
        {
          "renderer": "colorpicker",
          "name": "color",
          "label": "颜色"
        },
        {
          "renderer": "rate",
          "name": "rate",
          "label": "评论"
        },
        {
          "renderer": "input",
          "name": "textarea",
          "label": "文本框",
          "placeholder": "占位符",
          "type": "textarea",
          "validType": "normal"
        },
        {
          "renderer": "combo",
          "name": "array",
          "label": "简单数组",
          "requiredOn": "1",
          "inline": false,
          "labelWidth": "80px",
          "type": "array"
        },
        {
          "renderer": "combo",
          "name": "custom",
          "label": "复合字典",
          "requiredOn": "1",
          "inline": false,
          "type": "custom",
          "labelWidth": "80px",
          "controls": [
            {
              "renderer": "input",
              "label": "aaa",
              "name": "aaa",
              "requiredOn": "1"
            },
            {
              "renderer": "input",
              "label": "bbb",
              "name": "bbb",
              "requiredOn": "1"
            }
          ]
        },
        {
          "renderer": "combo",
          "name": "multiplle",
          "label": "复合数组",
          "requiredOn": 1,
          "inline": false,
          "type": "multiple",
          "labelWidth": "80px",
          "controls": [
            {
              "renderer": "radio",
              "label": "radio",
              "name": "radio",
              "requiredOn": "1"
            },
            {
              "renderer": "input",
              "label": "input",
              "name": "input",
              "requiredOn": "1"
            }
          ]
        },
        {
          "renderer": "combo",
          "name": "map",
          "label": "简单字典",
          "labelWidth": "80px",
          "requiredOn": "1",
          "inline": false,
          "type": "map"
        },
        {
          "renderer": "list",
          "name": "ll",
          "label": "多选组",
          "options": [
            {
              "name": "key1",
              "value": "value1",
              "src": "",
              "title": "项1",
              "desc": "这是选项1"
            },
            {
              "name": "key2",
              "value": "value2",
              "src": "",
              "title": "项2",
              "desc": "那是选项2"
            }
          ],
          "body": [
            {
              "renderer": "avatar",
              "shape": "square",
              "borderRadius": 0,
              "color": "#606266",
              "bgColor": "#ffffff",
              "icon": "Avatar",
              "invert": true
            },
            {
              "renderer": "html",
              "html": "<div class='ulist-demo'><h4><%=data.title%></h4><span><%=data.desc%></span></div>"
            }
          ],
          "rules": []
        },
        {
          "renderer": "upload",
          "name": "image",
          "preview": true,
          "validType": "normal",
          "listType": "picture-card",
          "rules": [
            {
              "required": false
            }
          ],
          "autoUpload": true,
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload",
          "limit": 1,
          "feedbackConfig": [],
          "accept": ".webp,.jpg,.png",
          "spliced": false,
          "astrict": {
            "maxByte": "10000",
            "minWidth": "120"
          },
          "label": "图片",
          "data": {},
          "requiredOn": "true"
        },
        {
          "renderer": "upload",
          "name": "video",
          "preview": true,
          "validType": "normal",
          "type": "video",
          "listType": "picture-card",
          "rules": [
            {
              "required": false
            }
          ],
          "autoUpload": true,
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload?type=video",
          "limit": 1,
          "feedbackConfig": [],
          "accept": ".mp4",
          "spliced": false,
          "astrict": {
            "maxByte": "10000"
          },
          "label": "视频",
          "data": {},
          "requiredOn": "true"
        },
        {
          "renderer": "upload",
          "listType": "picture-card",
          "name": "files",
          "label": "文件",
          "autoUpload": true,
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload?type=file",
          "accept": ".zip,.txt",
          "type": "file",
          "spliced": false,
          "preview": true,
          "astrict": {
            "maxByte": "50000"
          },
          "data": {},
          "feedbackConfig": []
        },
        {
          "renderer": "upload",
          "listType": "picture-card",
          "name": "manual",
          "label": "切片",
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload?type=file",
          "accept": ".mp4",
          "type": "video",
          "spliced": true,
          "preview": true,
          "astrict": {
            "minByte": "100"
          },
          "data": {},
          "feedbackConfig": []
        },
        {
          "renderer": "viewer",
          "body": [
            {
              "renderer": "data"
            }
          ],
          "target": ".i-admin__main",
          "classname": "swing_skew_1"
        },
        {
          "renderer": "divider",
          "text": "扩展控件"
        },
        {
          "renderer": "calendar",
          "name": "calendar",
          "required": true,
          "initApi": {
            "url": "/api/mock/calendar",
            "method": "get",
            "params": {
              "curDatePrefix": "${curDatePrefix}"
            }
          },
          "body": [
            {
              "renderer": "html",
              "classname": "demo-form-calendar__html",
              "html": "<%=data.value%>"
            }
          ],
          "label": "排期"
        },
        {
          "renderer": "Schedule",
          "name": "schedule_date",
          "label": "可选时间",
          "defaultValue": {},
          "requiredOn": "true"
        },
        {
          "renderer": "Richtxt",
          "name": "richtext",
          "label": "富文本",
          "requiredOn": "true"
        },
        {
          "renderer": "action",
          "actionType": "submit",
          "text": "提交",
          "initApi": {
            "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/calendar",
            "method": "get",
            "params": {}
          },
          "silentMessage": true,
          "silentLoading": false,
          "cached": false,
          "syncLocation": false,
          "plain": true,
          "body": {},
          "type": "primary",
          "data": {}
        },
        {
          "renderer": "action",
          "text": "重置",
          "type": "warning",
          "plain": true,
          "actionType": "reset",
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {},
          "popupType": "confirm",
          "confirmTitle": "确定要重置吗？"
        }
      ],
      "labelWidth": "120px",
      "idem": true,
      "classname": "actions-fixed"
    }
  ],
  "steps": []
}
