data.pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "grid",
      "span": "24",
      "body": [
        {
          "renderer": "notice",
          "type": "warning",
          "effect": "dark",
          "closable": true,
          "title": "全心全意为用户服务！"
        }
      ]
    },
    {
      "renderer": "grid",
      "span": "6,12,6",
      "body": [
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "html",
              "html": "聊天记录",
              "classname": "margin-bottom-20 strong"
            },
            {
              "renderer": "service",
              "initApi": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/wechats",
                "method": "get",
                "params": {}
              },
              "body": {
                "renderer": "each",
                "name": "rows",
                "body": {
                  "renderer": "avatar",
                  "title": "<%=data.userId%>",
                  "subTitle": "<%=data.message%>",
                  "description": "<%=data.date%>"
                }
              }
            }
          ],
          "classname": "live-video__wechats",
          "footer": [
            {
              "renderer": "form",
              "controls": [
                {
                  "renderer": "input",
                  "name": "message",
                  "placeholder": "请输入",
                  "type": "text",
                  "validType": "normal",
                  "rules": []
                },
                {
                  "renderer": "action",
                  "text": "发布",
                  "actionType": "submit",
                  "actionApi": {
                    "url": "",
                    "method": "post",
                    "params": {}
                  },
                  "params": {},
                  "body": {}
                }
              ],
              "actionApi": {
                "url": "/submit",
                "method": "post",
                "params": {}
              },
              "inline": true
            }
          ]
        },
        {
          "renderer": "wrapper",
          "body": [
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "html",
                  "html": "直播预览",
                  "classname": "margin-bottom-20 strong"
                },
                {
                  "renderer": "video",
                  "src": "https://cdn.jsdelivr.net/gh/xdlumia/files/video-play/IronMan.mp4",
                  "poster": "https://cdn.jsdelivr.net/gh/xdlumia/files/video-play/ironMan.jpg",
                  "width": 546
                },
                {
                  "renderer": "avatar",
                  "title": "令狐瓜子 直播间",
                  "icon": "Avatar",
                  "size": 25,
                  "shape": "circle",
                  "borderRadius": 0,
                  "color": "#606266",
                  "bgColor": "#ffffff",
                  "classname": "margin-top-20"
                },
                {
                  "renderer": "html",
                  "html": "36,000在看",
                  "classname": "video-live__audience"
                }
              ],
              "classname": "margin-bottom-20"
            },
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "tabs",
                  "panels": [
                    {
                      "label": "直播方式",
                      "icon": "",
                      "body": [
                        {
                          "renderer": "form",
                          "controls": [
                            {
                              "renderer": "radio",
                              "name": "type",
                              "type": "button",
                              "target": "LiveVideoTable",
                              "options": [
                                {
                                  "text": "普通直播",
                                  "value": "1"
                                },
                                {
                                  "text": "控流直播",
                                  "value": "2"
                                },
                                {
                                  "text": "视频直播",
                                  "value": "3",
                                  "disabledOn": ""
                                },
                                {
                                  "text": "网页开播",
                                  "value": "4",
                                  "disabledOn": ""
                                }
                              ],
                              "defaultValue": "3"
                            }
                          ],
                          "submitType": "target",
                          "actionApi": {},
                          "target": "LiveVideoTable"
                        },
                        {
                          "renderer": "table",
                          "name": "LiveVideoTable",
                          "columns": [
                            {
                              "fixed": false,
                              "sortable": false,
                              "type": "index"
                            },
                            {
                              "label": "封面",
                              "name": "poster",
                              "body": [
                                {
                                  "renderer": "image",
                                  "src": "${cover}",
                                  "previews": []
                                }
                              ]
                            },
                            {
                              "label": "名称",
                              "name": "name"
                            },
                            {
                              "label": "视频时长",
                              "name": "length"
                            },
                            {
                              "label": "视频ID",
                              "name": "id"
                            }
                          ],
                          "actions": [],
                          "pageConfig": {
                            "pageIndex": "pageIndex",
                            "pageSize": "pageSize",
                            "total": "total",
                            "rows": "rows"
                          },
                          "initApi": {
                            "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/videos",
                            "method": "get",
                            "params": {
                              "type": "${type}",
                              "timestamp": "${timestamp}"
                            }
                          },
                          "initData": {
                            "type": 3
                          },
                          "height": 320,
                          "showDynamicColumn": false,
                          "showHeader": false
                        }
                      ]
                    },
                    {
                      "label": "在线人数",
                      "icon": "",
                      "body": []
                    }
                  ],
                  "activeIndex": 1,
                  "tabPosition": "top"
                }
              ]
            }
          ]
        },
        {
          "renderer": "service",
          "initApi": {
            "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/video/stats",
            "method": "get",
            "params": {}
          },
          "body": [
            {
              "renderer": "card",
              "classname": "margin-bottom-20",
              "body": [
                {
                  "renderer": "html",
                  "html": "直播状态",
                  "classname": "strong margin-bottom-10"
                },
                {
                  "renderer": "static",
                  "columns": [
                    {
                      "label": "主流码率",
                      "name": "mainBit"
                    },
                    {
                      "label": "主流帧率",
                      "name": "mainFrame"
                    },
                    {
                      "label": "热备码率",
                      "name": "hotDeviceBit"
                    },
                    {
                      "label": "热备帧率",
                      "name": "hotDeviceFrmae"
                    },
                    {
                      "label": "冷备码率",
                      "name": "coldDeviceBit"
                    },
                    {
                      "label": "冷备帧率",
                      "name": "coldDeviceFrame"
                    }
                  ],
                  "column": 2
                },
                {
                  "renderer": "html",
                  "html": "画面信息",
                  "classname": "strong margin-bottom-10"
                },
                {
                  "renderer": "static",
                  "columns": [
                    {
                      "label": "线路",
                      "name": "line"
                    },
                    {
                      "label": "CDN",
                      "name": "cdn"
                    },
                    {
                      "label": "播放格式",
                      "name": "type"
                    },
                    {
                      "label": "画质",
                      "name": "quality"
                    }
                  ],
                  "column": 2
                }
              ],
              "footer": []
            },
            {
              "renderer": "card",
              "classname": "margin-bottom-20",
              "body": [
                {
                  "renderer": "html",
                  "html": "快捷操作",
                  "classname": "strong margin-bottom-10"
                },
                {
                  "renderer": "action",
                  "text": "切换清晰度",
                  "classname": "video-live__action"
                },
                {
                  "renderer": "action",
                  "text": "设备流切换",
                  "classname": "video-live__action"
                },
                {
                  "renderer": "action",
                  "text": "摘除清晰度",
                  "classname": "video-live__action"
                },
                {
                  "renderer": "action",
                  "text": "推流垫片",
                  "classname": "video-live__action"
                }
              ]
            },
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "html",
                  "html": "直播信息",
                  "classname": "strong margin-bottom-10"
                },
                {
                  "renderer": "form",
                  "controls": [
                    {
                      "renderer": "input",
                      "name": "title",
                      "label": "直播标题",
                      "type": "text",
                      "clearable": true,
                      "validType": "normal",
                      "rules": []
                    },
                    {
                      "renderer": "input",
                      "name": "notice",
                      "label": "上线通知",
                      "type": "textarea",
                      "clearable": true,
                      "validType": "normal",
                      "rules": []
                    },
                    {
                      "renderer": "input",
                      "name": "typ",
                      "label": "直播类目",
                      "type": "text",
                      "clearable": true,
                      "validType": "normal",
                      "rules": []
                    },
                    {
                      "renderer": "action",
                      "text": "更新",
                      "actionType": "submit"
                    }
                  ],
                  "labelPosition": "top",
                  "actionApi": {
                    "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/number",
                    "method": "post",
                    "params": {}
                  }
                }
              ],
              "footer": []
            }
          ]
        }
      ],
      "gutter": 20
    }
  ],
  "steps": [],
  "innerStyle": ".i-website__main {\n    background: #f6f6f6;\n}\n.live-video__wechats {\n    .i-avatar__content {\n    .i-avatar__title {\n    margin-bottom: 4px;\n    font-size: 12px;\n    color: var(--el-color-warning);\n}\n.i-avatar__subtitle {\n    margin-bottom: 4px;\n    font-size: 12px;\n}\n.i-avatar__description {\n    margin-bottom: 10px;\n    font-size: 12px;\n}\n}\n}\n.video-live__audience {\n    position: absolute;\n    right: 20px;\n    bottom: 20px;\n}\n.video-live__action {\n    width: 100%;\n    margin-left: 0 !important;\n    margin-bottom: 10px;\n}"
}
