pageSchema:
{
  "renderer": "page",
  "initData": {
  },
  "body": [
    {
      "renderer": "form",
      "disabledOn": "data.isFormDisabled",
      "actionApi": {
        "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/form/submit",
        "params": {
          "*": "*"
        },
        "method": "post"
      },
      "controls": [
        {
          "renderer": "divider",
          "text": "基础控件"
        },
        {
          "renderer": "input",
          "name": "name",
          "label": "姓名",
          "tip": "请填写姓名",
          "placeholder": "请填写姓名",
          "requiredOn": true,
          "type": "text",
          "validType": "normal"
        },
        {
          "renderer": "switch",
          "defaultValue": false,
          "confirmTitle": "确定吗？",
          "rules": [],
          "label": "开 关",
          "name": "switch1"
        },
        {
          "renderer": "switch",
          "name": "switch_check",
          "label": "开关选择框",
          "type": "checkbox"
        },
        {
          "renderer": "switch",
          "name": "switch_button",
          "label": "开关按钮",
          "type": "button",
          "defaultValue": false,
          "activeText": "开 启",
          "inactiveText": "关 闭",
          "rules": []
        },
        {
          "renderer": "input",
          "name": "asyncValid",
          "label": "异步校验",
          "tip": "异步校验",
          "placeholder": "异步校验",
          "type": "number",
          "validType": "async",
          "rules": [
            {
              "exp": {
                "url": "/api/mock/form/verify",
                "method": "get",
                "params": {
                  "asyncValid": "${asyncValid}"
                }
              },
              "trigger": "blur"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "sex",
          "label": "性别",
          "tip": "性别",
          "defaultValue": "0",
          "options": [
            {
              "value": "1",
              "text": "男"
            },
            {
              "value": "0",
              "text": "女"
            }
          ]
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 1",
          "options": [
            {
              "value": "1",
              "text": "爷爷"
            },
            {
              "value": "2",
              "text": "大伯"
            },
            {
              "value": "3",
              "text": "叔叔"
            },
            {
              "value": "4",
              "text": "哥哥"
            },
            {
              "value": "5",
              "text": "弟弟"
            }
          ]
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 0",
          "options": [
            {
              "value": "6",
              "text": "奶奶"
            },
            {
              "value": "7",
              "text": "大姑"
            },
            {
              "value": "8",
              "text": "小姑"
            },
            {
              "value": "9",
              "text": "姐姐"
            },
            {
              "value": "0",
              "text": "妹妹"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "connect",
          "label": "联系方式",
          "tip": "联系方式",
          "defaultValue": "0",
          "options": [
            {
              "value": "0",
              "text": "电话"
            },
            {
              "value": "1",
              "text": "邮箱"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "mail",
          "label": "邮件",
          "tip": "填写邮箱",
          "visibleOn": "data.connect == 1",
          "placeholder": "请填写邮箱"
        },
        {
          "renderer": "input",
          "name": "phone",
          "label": "电话",
          "tip": "预留电话",
          "visibleOn": "data.connect == 0",
          "placeholder": "请填写电话"
        },
        {
          "renderer": "datepicker",
          "name": "createdAt",
          "tip": "请选择派送日期",
          "type": "datetime",
          "readonly": false,
          "disabledOn": "data.name=='shuzhong'",
          "label": "日期选择器3"
        },
        {
          "renderer": "switch",
          "name": "adult",
          "label": "成年",
          "defaultValue": true,
          "tip": "已满十八岁"
        },
        {
          "renderer": "colorpicker",
          "name": "color",
          "label": "颜色",
          "rules": [
            {
              "required": true,
              "message": "评论不能为空"
            }
          ]
        },
        {
          "renderer": "rate",
          "name": "rate",
          "label": "评论",
          "rules": [
            {
              "required": true,
              "message": "评论不能为空"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "textarea",
          "label": "文本框",
          "placeholder": "占位符",
          "type": "textarea",
          "validType": "normal",
          "rules": [
            {
              "required": false
            }
          ]
        },
        {
          "renderer": "upload",
          "name": "image",
          "preview": true,
          "validType": "normal",
          "listType": "picture-card",
          "rules": [
            {
              "required": false
            }
          ],
          "autoUpload": true,
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload",
          "limit": 1,
          "feedbackConfig": [],
          "accept": ".webp,.jpg,.png",
          "spliced": false,
          "astrict": {
            "maxByte": "10000",
            "minWidth": "120"
          },
          "label": "图片",
          "data": {},
          "requiredOn": "true"
        },
        {
          "renderer": "upload",
          "name": "video",
          "preview": true,
          "validType": "normal",
          "type": "video",
          "listType": "picture-card",
          "rules": [
            {
              "required": false
            }
          ],
          "autoUpload": true,
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload?type=video",
          "limit": 1,
          "feedbackConfig": [],
          "accept": ".mp4",
          "spliced": false,
          "astrict": {
            "maxByte": "10000"
          },
          "label": "视频",
          "data": {},
          "requiredOn": "true"
        },
        {
          "renderer": "upload",
          "listType": "picture-card",
          "name": "files",
          "label": "文件",
          "autoUpload": true,
          "actionUrl": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/upload?type=file",
          "accept": ".zip,.txt",
          "type": "file",
          "spliced": false,
          "preview": true,
          "astrict": {
            "maxByte": "50000"
          },
          "data": {},
          "feedbackConfig": []
        },
        {
          "renderer": "viewer",
          "body": [
            {
              "renderer": "data"
            }
          ],
          "target": ".i-admin__main"
        },
        {
          "renderer": "divider",
          "text": "扩展控件"
        },
        {
          "renderer": "calendar",
          "name": "calendar",
          "required": true,
          "initApi": {
            "url": "/api/mock/calendar",
            "method": "get",
            "params": {
              "curDatePrefix": "${curDatePrefix}"
            }
          },
          "body": [
            {
              "renderer": "html",
              "classname": "demo-form-calendar__html",
              "html": "<%=data.value%>"
            }
          ],
          "label": "排期"
        },
        {
          "renderer": "Schedule",
          "name": "schedule_date",
          "label": "可选时间",
          "defaultValue": {}
        },
        {
          "renderer": "Richtxt",
          "name": "richtext",
          "label": "富文本"
        },
        {
          "renderer": "action",
          "actionType": "submit",
          "text": "提交",
          "initApi": {
            "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/calendar",
            "method": "get",
            "params": {}
          },
          "silentMessage": true,
          "silentLoading": false,
          "cached": false,
          "syncLocation": false,
          "plain": true,
          "body": {},
          "type": "primary",
          "data": {}
        },
        {
          "renderer": "action",
          "text": "重置",
          "type": "warning",
          "plain": true,
          "actionType": "reset",
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {},
          "popupType": "confirm",
          "confirmTitle": "确定要重置吗？"
        }
      ],
      "labelWidth": "120px",
      "idem": true,
      "classname": "actions-fixed"
    }
  ],
  "steps": []
}
