data.pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "https://raw.githubusercontent.com/songshuzhong/i-components/master/src/components/ShoppingCart/ShoppingCart.vue",
      "body": [
        {
          "renderer": "tabs",
          "panels": [
            {
              "label": "商城",
              "icon": "House",
              "keepAlive": true,
              "body": [
                {
                  "renderer": "form",
                  "sticky": true,
                  "scrollbar": ".el-tabs__content",
                  "controls": [
                    {
                      "renderer": "input",
                      "name": "search",
                      "placeholder": "推荐搜索 关键字",
                      "prefixIcon": "Search",
                      "type": "text",
                      "rules": []
                    }
                  ],
                  "submitType": "target",
                  "target": "ShoppingCartData",
                  "classname": "search-form"
                },
                {
                  "renderer": "carousel",
                  "body": [
                    {
                      "renderer": "image",
                      "src": "https://gankai.gitee.io/vue-jd-h5/img/banner6.3b281aa3.jpg",
                      "previews": []
                    },
                    {
                      "renderer": "image",
                      "src": "https://gankai.gitee.io/vue-jd-h5/img/banner8.2b15b757.jpg",
                      "previews": []
                    }
                  ],
                  "autoplay": true,
                  "height": 254
                },
                {
                  "renderer": "notice",
                  "effect": "light",
                  "direction": "vertical",
                  "title": [
                    "您的订单正在出库，请耐心等待",
                    "您的订单在【苏州市发往北京市】的列车上，已发出"
                  ]
                },
                {
                  "renderer": "cards",
                  "initApi": {
                    "url": "/api/mock/products",
                    "method": "get",
                    "params": {}
                  },
                  "gutter": 10,
                  "body": [
                    {
                      "renderer": "image",
                      "src": "${cover}",
                      "previews": [],
                      "fit": "scale-down"
                    },
                    {
                      "renderer": "html",
                      "html": "<%=data.name%>",
                      "classname": "font-weight-600 text-center zoom_3"
                    },
                    {
                      "renderer": "action",
                      "text": "￥<%=data.price%>",
                      "actionType": "linkage",
                      "actionApi": {
                        "url": "/api/mock/shopping/price?productId=${productId}",
                        "method": "post",
                        "params": {}
                      },
                      "target": "ShoppingCartData",
                      "body": {},
                      "category": "image",
                      "size": 24,
                      "icon": "https://img13.360buyimg.com/imagetools/jfs/t1/211015/13/26941/4059/63369204Ee33e6bce/78dda240fdc6ae88.png",
                      "tag": "div"
                    }
                  ],
                  "shadow": "hover",
                  "skeletons": 4,
                  "inherit": {
                    "type": "none",
                    "value": []
                  },
                  "initData": {},
                  "virtual": false,
                  "reactive": false
                }
              ]
            },
            {
              "label": "购物车",
              "icon": "ShoppingCart",
              "keepAlive": false,
              "badge": "data.ShoppingCartData.length",
              "body": [
                {
                  "renderer": "form",
                  "name": "ShoppingCartDataForm",
                  "target": "ShoppingCartData?merge",
                  "silentLoading": true,
                  "actionApi": {
                    "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/shoppingcart/info",
                    "method": "post",
                    "cached": false,
                    "params": {
                      "*": "*"
                    }
                  },
                  "controls": [
                    {
                      "renderer": "combo",
                      "name": "ShoppingCartData",
                      "inline": false,
                      "type": "multiple",
                      "controls": [
                        {
                          "renderer": "avatar",
                          "title": "<%=data.name%>",
                          "subTitle": "<%=data.price%>",
                          "description": "<%=data.desc%>",
                          "src": "${cover}",
                          "isComputedSrc": true,
                          "size": 70,
                          "shape": "square",
                          "borderRadius": 0,
                          "color": "#606266",
                          "bgColor": "#ffffff",
                          "body": [],
                          "classname": "back_move_4"
                        },
                        {
                          "renderer": "number",
                          "label": "",
                          "idem": true,
                          "size": "small",
                          "name": "count"
                        }
                      ]
                    },
                    {
                      "renderer": "wrapper",
                      "classname": "market-form-footer",
                      "body": [
                        {
                          "renderer": "html",
                          "html": "<span>合计：</span>￥<%=data.totalPrice%>"
                        },
                        {
                          "renderer": "action",
                          "actionType": "submit",
                          "remoteComponent": "ShoppingCartDataForm",
                          "text": "结算"
                        }
                      ]
                    }
                  ],
                  "initApi": {
                    "url": "/api/mock/shopping/price",
                    "method": "post",
                    "cached": false,
                    "params": {
                      "*": "*"
                    }
                  },
                  "classname": "market-form"
                }
              ]
            },
            {
              "label": "我",
              "icon": "Avatar",
              "keepAlive": true,
              "body": [
                {
                  "renderer": "card",
                  "header": [
                    {
                      "renderer": "image",
                      "src": "https://songshuzhong.github.io/i-assets/public/logo/me.jpg",
                      "previews": [],
                      "classname": "pane-3-avatar"
                    },
                    {
                      "renderer": "html",
                      "html": "钻石王老五",
                      "classname": "pane-3-title",
                      "inline": true
                    },
                    {
                      "renderer": "action",
                      "text": "分享节点",
                      "icon": "Share",
                      "body": {},
                      "classname": "pane-3-share",
                      "isText": true
                    },
                    {
                      "renderer": "action",
                      "text": "商家节点",
                      "body": {},
                      "classname": "pane-3-share1",
                      "type": "success",
                      "isText": true
                    }
                  ],
                  "classname": "pane-3-card1",
                  "body": [
                    {
                      "renderer": "statistic",
                      "value": "shangpin",
                      "name": "6190",
                      "title": "商品关注"
                    },
                    {
                      "renderer": "statistic",
                      "value": "shangpin",
                      "name": "2210",
                      "title": "商铺关注"
                    },
                    {
                      "renderer": "statistic",
                      "value": "shangpin",
                      "name": "890",
                      "title": "足迹"
                    }
                  ],
                  "footer": [
                    {
                      "renderer": "action",
                      "text": "待付款",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "category": "image",
                      "icon": "https://cdnstyle.woqukaoqin.com/image/form-icon/wx_wodejiaqi%20%403x.png",
                      "classname": "vertical"
                    },
                    {
                      "renderer": "action",
                      "text": "待发货",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "category": "image",
                      "icon": "https://cdnstyle.woqukaoqin.com/image/form-icon/goout.png",
                      "classname": "vertical"
                    },
                    {
                      "renderer": "action",
                      "text": "待收货",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "category": "image",
                      "icon": "https://cdnstyle.woqukaoqin.com/image/form-icon/wx_wodejiaqi%20%403x.png",
                      "classname": "vertical"
                    },
                    {
                      "renderer": "action",
                      "text": "退换/售后",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "category": "image",
                      "icon": "https://cdnstyle.woqukaoqin.com/image/form-icon/discard_apply_2x.png",
                      "classname": "vertical"
                    }
                  ],
                  "shadow": "never"
                },
                {
                  "renderer": "card",
                  "body": [
                    {
                      "renderer": "action",
                      "text": "我的钱包",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "Wallet",
                      "tag": "div",
                      "classname": "pane-3-card2-action",
                      "isText": true
                    },
                    {
                      "renderer": "action",
                      "text": "节点申请",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "Notebook",
                      "classname": "pane-3-card2-action",
                      "isText": true,
                      "tag": "div"
                    },
                    {
                      "renderer": "action",
                      "text": "商家入驻",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "Management",
                      "classname": "pane-3-card2-action",
                      "isText": true,
                      "tag": "div"
                    },
                    {
                      "renderer": "action",
                      "text": "分享链接",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "Share",
                      "classname": "pane-3-card2-action",
                      "isText": true,
                      "tag": "div"
                    }
                  ],
                  "classname": "pane-3-card2",
                  "shadow": "never"
                },
                {
                  "renderer": "card",
                  "body": [
                    {
                      "renderer": "action",
                      "text": "收货地址",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "LocationInformation",
                      "tag": "div",
                      "classname": "pane-3-card2-action",
                      "isText": true
                    },
                    {
                      "renderer": "action",
                      "text": "消息中心",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "InfoFilled",
                      "classname": "pane-3-card2-action",
                      "isText": true,
                      "tag": "div"
                    },
                    {
                      "renderer": "action",
                      "text": "帮助中心",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "QuestionFilled",
                      "classname": "pane-3-card2-action",
                      "isText": true,
                      "tag": "div"
                    },
                    {
                      "renderer": "action",
                      "text": "设置",
                      "actionType": "copy",
                      "content": "安师大发",
                      "body": {},
                      "icon": "Setting",
                      "classname": "pane-3-card2-action",
                      "isText": true,
                      "tag": "div"
                    }
                  ],
                  "classname": "pane-3-card2",
                  "shadow": "never"
                }
              ]
            }
          ],
          "tabPosition": "bottom",
          "stretch": true,
          "activeTab": 0,
          "type": "border-card"
        }
      ],
      "primaryKey": "productId",
      "name": "ShoppingCartData"
    }
  ],
  "classname": "shopping-cart-page",
  "innerStyle": ".shopping-cart-page {\n  .search-form {\n    position: relative;\n    padding-top: 15px;\n    margin-bottom: -67px;\n    z-index: 9999;\n  }\n  .i-notice__container {\n    border-radius: 0;\n  }\n  .market-form {\n    position: unset;\n    height: 100%;\n    padding-bottom: 50px;\n    overflow: scroll;\n    .i-avatar__logo {\n      padding-top: 10px;\n    }\n    .el-input-number--small {\n      width: 120px;\n      line-height: 0.58667rem;\n      margin-top: -60px;\n      margin-left: 190px;\n    }\n    .i-form__combo__plus {\n      display: none;\n    }\n    .i-form__field__wrapper {\n      position: absolute;\n      bottom: 0;\n      width: 100%;\n      padding: 2px 10px !important;\n      margin-bottom: 0;\n      background: var(--el-color-white);\n      z-index: 100;\n      .i-wrapper__container__body {\n        display: flex;\n        align-items: center;\n        justify-content: space-between;\n      }\n    }\n  }\n  .i-tabs__container {\n    height: 100%;\n    .pane-0 {\n      .el-card {\n        .el-image {\n          width: 120px;\n        }\n      }\n      .i-crud__container {\n        &.cards {\n          padding: 0 10px;\n        }\n      }\n      .i-action__avatar {\n        width: 100%;\n        transform: scale(0.8);\n        .i-avatar__logo {\n          width: 100%;\n        }\n        .i-avatar__content {\n          position: relative;\n          margin-left: -25px;\n          padding-top: 2px;\n          z-index: 99;\n          color: white;\n        }\n        .el-avatar {\n          position: relative;\n          left: 50%;\n          width: 140px;\n          transform: translateX(-50%);\n          background: white;\n        }\n      }\n    }\n    .pane-1 {\n      overflow: hidden;\n    }\n  }\n  .el-tabs__content {\n    height: calc(100% - 40px);\n    padding: 0;\n    overflow: scroll;\n  }\n  .el-tabs__header {\n    position: absolute;\n    bottom: 0;\n    width: 100%;\n    &.is-bottom {\n      border-bottom: 1px solid var(--el-border-color) !important;\n    }\n  }\n  .pane-3-card1 {\n    border-radius: 0;\n    background: transparent;\n    .i-card__body,\n    .i-card__footer {\n      display: flex;\n      justify-content: space-around;\n    }\n  }\n  .pane-3-avatar {\n    width: 70px;\n    height: 70px;\n    margin-left: 20px;\n    .el-image__inner {\n      border-radius: 100%;\n    }\n  }\n  .pane-3-title {\n    position: absolute;\n    top: 20px;\n    left: 130px;\n    font-size: 20px;\n    font-weight: 900;\n  }\n  .pane-3-share {\n    position: absolute;\n    top: 60px;\n    left: 130px;\n  }\n  .pane-3-share1 {\n    position: absolute;\n    top: 60px;\n    left: 210px;\n  }\n  .pane-3-card2 {\n    border: 0;\n    background: transparent;\n  }\n  .pane-3-card2-action {\n    width: 100%;\n    margin: 10px 20px;\n    span {\n      width: 100%;\n      font-size: 16px;\n    }\n  }\n}",
  "isH5": true
}
