data.pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "table",
      "name": "UserTable",
      "showExport": true,
      "height": 500,
      "initApi": {
        "url": "/api/users",
        "method": "get",
        "params": {
          "timestamp": "${timestamp}",
          "name": "${name}"
        }
      },
      "selectableOn": "data.name!='1345'",
      "columns": [
        {
          "label": "uid",
          "name": "uid",
          "body": [],
          "align": "none",
          "visibleOn": "",
          "width": 300,
          "fixed": "left"
        },
        {
          "label": "主人公",
          "name": "as",
          "columns": [
            {
              "name": "name",
              "label": " 姓名",
              "groupBy": "多媒体",
              "width": 300,
              "body": [
                {
                  "renderer": "quickedit",
                  "label": "<%=data.name%>",
                  "keyword": "name",
                  "rules": [
                    {
                      "exp": "!/^[a-zA-Z0-9_\\u4e00-\\u9fa5]+$/.test(data.name)",
                      "message": "必须是汉字、数字、字母、下划线",
                      "trigger": "blur"
                    }
                  ],
                  "actionApi": {
                    "url": "/api/user",
                    "method": "post",
                    "params": {
                      "name": "${name}",
                      "uid": "${uid}"
                    }
                  },
                  "target": "UserTable",
                  "title": "更新姓名",
                  "reload": "UserTable"
                }
              ],
              "align": "none",
              "sortable": true
            },
            {
              "label": "性别",
              "name": "sex",
              "fixed": false,
              "sortable": false,
              "groupBy": "多媒体",
              "width": 300,
              "type": "",
              "align": "none",
              "body": [
                {
                  "renderer": "mapping",
                  "map": {
                    "0": "女",
                    "1": "男"
                  },
                  "optionConfig": {},
                  "name": "sex"
                }
              ]
            }
          ]
        },
        {
          "label": "创建时间",
          "name": "createdAt",
          "width": 300,
          "fixed": false,
          "sortable": false,
          "type": "",
          "align": "none",
          "body": []
        },
        {
          "label": "更新时间",
          "name": "updatedAt",
          "fixed": false,
          "sortable": false,
          "type": "",
          "align": "none",
          "width": 300,
          "body": []
        },
        {
          "label": "操作",
          "name": "id",
          "width": 300,
          "body": [
            {
              "renderer": "action",
              "name": "UpdateAction",
              "text": "更新",
              "type": "success",
              "actionType": "dialog",
              "plain": true,
              "size": "small",
              "group": true,
              "body": {
                "title": "模态框",
                "direction": "ltr",
                "closeOnClickModal": true,
                "closeOnPressEscape": true,
                "footer": [
                  {
                    "renderer": "action",
                    "text": "更新",
                    "actionType": "submit",
                    "actionApi": {
                      "url": "/api/demo",
                      "method": "post",
                      "params": {}
                    },
                    "remoteComponent": "UpdateForm"
                  }
                ],
                "body": [
                  {
                    "renderer": "form",
                    "name": "UpdateForm",
                    "actionApi": {
                      "url": "/api/user?uid=${uid}",
                      "method": "put",
                      "params": {
                        "*": "*"
                      }
                    },
                    "controls": [
                      {
                        "renderer": "input",
                        "name": "name",
                        "label": "姓名",
                        "placeholder": "占位符",
                        "type": "text",
                        "validType": "normal"
                      }
                    ],
                    "reload": "UserTable"
                  }
                ],
                "fullscreen": false,
                "center": false,
                "renderer": "dialog",
                "autoNext": true
              },
              "actionApi": {
                "url": "",
                "method": "post",
                "params": {}
              }
            },
            {
              "renderer": "action",
              "size": "small",
              "group": true,
              "actionType": "drawer",
              "actionApi": {
                "url": "",
                "method": "post",
                "params": {}
              },
              "body": {
                "title": "模态框",
                "closeOnClickModal": true,
                "footer": [],
                "body": [
                  {
                    "renderer": "service",
                    "name": "ChartService",
                    "initApi": {
                      "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/chart/map",
                      "method": "get",
                      "params": {
                        "start_time": "${start_time}"
                      }
                    },
                    "body": [
                      {
                        "renderer": "chart",
                        "config": {
                          "tooltip": {
                            "trigger": "item"
                          },
                          "visualMap": {
                            "min": 800,
                            "max": 5000,
                            "text": [
                              "high",
                              "low"
                            ],
                            "realtime": false,
                            "calculable": true,
                            "inRange": {
                              "color": [
                                "#e6f7ff",
                                "#1890FF",
                                "#0050b3"
                              ]
                            }
                          },
                          "series": [
                            {
                              "type": "map",
                              "map": "china",
                              "name": "人均收入",
                              "data": "${list}"
                            }
                          ]
                        },
                        "height": 500,
                        "initData": {
                          "start_time": ""
                        },
                        "mapArea": "china"
                      },
                      {
                        "renderer": "chart",
                        "config": {
                          "series": [
                            {
                              "type": "graph",
                              "layout": "force",
                              "name": "特征",
                              "categories": [
                                {
                                  "name": "性格"
                                },
                                {
                                  "name": "工作态度"
                                },
                                {
                                  "name": "自我追求"
                                }
                              ],
                              "tooltip": {},
                              "itemStyle": {
                                "normal": {
                                  "label": {
                                    "show": true,
                                    "textStyle": {
                                      "color": "#333"
                                    }
                                  },
                                  "nodeStyle": {
                                    "brushType": "both",
                                    "borderColor": "rgba(255,215,0,0.4)",
                                    "borderWidth": 1
                                  },
                                  "linkStyle": {
                                    "type": "curve"
                                  }
                                },
                                "emphasis": {
                                  "label": {
                                    "show": false
                                  }
                                }
                              },
                              "nodes": [
                                {
                                  "category": 0,
                                  "name": "个人特质",
                                  "value": 40,
                                  "symbolSize": 40,
                                  "label": "个人特质",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "lightslategray"
                                  }
                                },
                                {
                                  "category": 1,
                                  "name": "性格",
                                  "value": 20,
                                  "symbolSize": 30,
                                  "label": "性格",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "#607d8b"
                                  }
                                },
                                {
                                  "category": 1,
                                  "name": "工作态度",
                                  "value": 20,
                                  "symbolSize": 30,
                                  "label": "工作态度",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "#607d8b"
                                  }
                                },
                                {
                                  "category": 1,
                                  "name": "自我追求",
                                  "value": 20,
                                  "symbolSize": 30,
                                  "label": "自我追求",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "#607d8b"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "主人公",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "主人公",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "时间观念",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "时间观念",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "闭环处理",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "闭环处理",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "设计优先",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "设计优先",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "P/PC平衡",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "P/PC平衡",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "保持敬畏",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "保持敬畏",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "有始有终",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "有始有终",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "事不过二",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "事不过二",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                },
                                {
                                  "category": 2,
                                  "name": "空杯心态",
                                  "value": 20,
                                  "symbolSize": 20,
                                  "label": "空杯心态",
                                  "draggable": true,
                                  "itemStyle": {
                                    "color": "##e0eaf0"
                                  }
                                }
                              ],
                              "links": [
                                {
                                  "source": "性格",
                                  "target": "个人特质"
                                },
                                {
                                  "source": "工作态度",
                                  "target": "个人特质"
                                },
                                {
                                  "source": "自我追求",
                                  "target": "个人特质"
                                },
                                {
                                  "source": "主人公",
                                  "target": "性格"
                                },
                                {
                                  "source": "时间观念",
                                  "target": "性格"
                                },
                                {
                                  "source": "保持敬畏",
                                  "target": "性格"
                                },
                                {
                                  "source": "闭环处理",
                                  "target": "工作态度"
                                },
                                {
                                  "source": "设计优先",
                                  "target": "工作态度"
                                },
                                {
                                  "source": "P/PC平衡",
                                  "target": "工作态度"
                                },
                                {
                                  "source": "有始有终",
                                  "target": "自我追求"
                                },
                                {
                                  "source": "事不过二",
                                  "target": "自我追求"
                                },
                                {
                                  "source": "空杯心态",
                                  "target": "自我追求"
                                }
                              ],
                              "force": {
                                "repulsion": 145
                              }
                            }
                          ]
                        },
                        "height": 500,
                        "initData": {
                          "start_time": ""
                        }
                      }
                    ]
                  }
                ],
                "renderer": "drawer"
              },
              "text": "查看",
              "plain": true,
              "params": {}
            },
            {
              "renderer": "action",
              "text": "删除",
              "group": true,
              "type": "warning",
              "plain": true,
              "size": "small",
              "popupType": "confirm",
              "confirmTitle": "确定删除吗？",
              "actionType": "ajax",
              "actionApi": {
                "url": "/api/user?uid=${uid}",
                "method": "delete",
                "params": {}
              },
              "reload": "UserTable",
              "params": {},
              "body": {}
            }
          ]
        }
      ],
      "initData": {
        "timestamp": "1"
      },
      "actions": [
        {
          "renderer": "action",
          "text": "批量删除",
          "size": "small",
          "actionType": "ajax",
          "actionApi": {
            "url": "/api/users",
            "method": "delete",
            "params": {
              "*": "*"
            }
          },
          "params": {},
          "body": {},
          "plain": true,
          "disabledOn": "!data.selectedRows.length",
          "icon": "Delete"
        },
        {
          "renderer": "action",
          "size": "small",
          "icon": "Printer",
          "actionType": "print",
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {},
          "text": "打印",
          "plain": true,
          "target": "UserTable"
        }
      ],
      "updateOnRowId": "uid",
      "title": "出场人物",
      "reactive": true,
      "lazy": true,
      "sticky": true
    },
    {
      "renderer": "form",
      "target": "UserTable",
      "actionApi": {
        "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/form/submit",
        "params": {},
        "method": "post"
      },
      "controls": [
        {
          "renderer": "input",
          "name": "name",
          "label": "姓名",
          "tip": "请填写姓名",
          "placeholder": "请填写姓名",
          "requiredOn": true,
          "type": "text",
          "validType": "normal"
        },
        {
          "renderer": "input",
          "name": "asyncValid",
          "label": "异步校验",
          "tip": "异步校验",
          "placeholder": "异步校验",
          "type": "number",
          "validType": "async",
          "rules": [
            {
              "exp": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/form/verify?asyncValid=${asyncValid}",
              "type": "api",
              "trigger": "blur"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "sex",
          "label": "性别",
          "tip": "性别",
          "defaultValue": "0",
          "options": [
            {
              "value": "1",
              "text": "男"
            },
            {
              "value": "0",
              "text": "女"
            }
          ]
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 1",
          "options": [
            {
              "value": "1",
              "text": "爷爷"
            },
            {
              "value": "2",
              "text": "大伯"
            },
            {
              "value": "3",
              "text": "叔叔"
            },
            {
              "value": "4",
              "text": "哥哥"
            },
            {
              "value": "5",
              "text": "弟弟"
            }
          ],
          "multiple": true,
          "optionConfig": {
            "text": "text",
            "value": "value"
          }
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 0",
          "options": [
            {
              "value": "6",
              "text": "奶奶"
            },
            {
              "value": "7",
              "text": "大姑"
            },
            {
              "value": "8",
              "text": "小姑"
            },
            {
              "value": "9",
              "text": "姐姐"
            },
            {
              "value": "0",
              "text": "妹妹"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "connect",
          "label": "联系方式",
          "tip": "联系方式",
          "defaultValue": "0",
          "options": [
            {
              "value": "0",
              "text": "电话"
            },
            {
              "value": "1",
              "text": "邮箱"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "mail",
          "label": "邮件",
          "tip": "填写邮箱",
          "visibleOn": "data.connect == 1",
          "placeholder": "请填写邮箱"
        },
        {
          "renderer": "input",
          "name": "phone",
          "label": "电话",
          "tip": "预留电话",
          "visibleOn": "data.connect == 0",
          "placeholder": "请填写电话"
        },
        {
          "renderer": "datepicker",
          "name": "createdAt",
          "tip": "请选择派送日期",
          "type": "datetime",
          "readonly": false,
          "disabledOn": "data.name=='shuzhong'",
          "format": "YYYY-MM-DD",
          "label": "日期1"
        },
        {
          "renderer": "switch",
          "name": "adult",
          "label": "成年",
          "defaultValue": true,
          "tip": "已满十八岁"
        },
        {
          "renderer": "colorpicker",
          "name": "color",
          "label": "颜色",
          "rules": [
            {
              "required": true,
              "message": "评论不能为空"
            }
          ]
        },
        {
          "renderer": "rate",
          "name": "rate",
          "label": "评论",
          "rules": [
            {
              "required": true,
              "message": "评论不能为空"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "textarea",
          "label": "文本框",
          "placeholder": "占位符",
          "type": "textarea",
          "validType": "normal",
          "rules": [
            {
              "required": false
            }
          ]
        },
        {
          "renderer": "action",
          "actionType": "submit",
          "text": "提交",
          "initApi": {
            "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/calendar",
            "method": "get",
            "params": {}
          },
          "silentMessage": true,
          "silentLoading": false,
          "cached": false,
          "syncLocation": false,
          "plain": true,
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {}
        },
        {
          "renderer": "action",
          "text": "重置",
          "type": "warning",
          "plain": true,
          "actionType": "reset",
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {},
          "popupType": "confirm",
          "confirmTitle": "确定要重置吗？"
        }
      ],
      "labelPosition": "top"
    },
    {
      "renderer": "visibility",
      "body": [
        {
          "renderer": "alert"
        }
      ]
    }
  ],
  "initApi": {
    "url": "",
    "method": "get",
    "params": {}
  },
  "initData": {
    "name": "",
    "type": "2"
  },
  "mergeUrlQuery": true,
  "steps": []
}
