data.pageSchema:
{
  "renderer": "page",
  "body": {
    "renderer": "steps",
    "actionApi": {
      "url": "/api/user",
      "params": {},
      "method": "post"
    },
    "redirect": "/demo/toolmaker",
    "controls": [
      {
        "renderer": "divider",
        "text": "第一步",
        "icon": "Edit",
        "name": "one"
      },
      {
        "renderer": "input",
        "name": "name",
        "label": "姓名",
        "tip": "姓名",
        "rules": [
          {
            "required": true,
            "message": "姓名不能为空"
          },
          {
            "required": false
          }
        ],
        "hiddenOn": true
      },
      {
        "renderer": "input",
        "name": "age",
        "label": "年龄",
        "tip": "年龄",
        "rules": [
          {
            "required": true,
            "message": "年龄不能为空"
          },
          {
            "required": false
          }
        ],
        "hiddenOn": true
      },
      {
        "renderer": "select",
        "name": "sex",
        "label": "性别",
        "tip": "活动范围在全宇宙",
        "options": [
          {
            "text": "男",
            "value": "1"
          },
          {
            "text": "女",
            "value": "0"
          }
        ],
        "hiddenOn": true
      },
      {
        "renderer": "divider",
        "text": "第二步",
        "icon": "Upload",
        "name": "two"
      },
      {
        "renderer": "input",
        "name": "province",
        "label": "省份",
        "tip": "请填写省份信息",
        "rules": [
          {
            "required": true,
            "message": "省份不能为空"
          },
          {
            "required": false
          }
        ],
        "hiddenOn": false
      },
      {
        "renderer": "input",
        "name": "city",
        "label": "城市",
        "tip": "请填写城市信息",
        "rules": [
          {
            "required": true,
            "message": "城市不能为空"
          },
          {
            "required": false
          },
          {
            "type": "api",
            "exp": "/api/valid?${name}",
            "trigger": "blur"
          }
        ],
        "hiddenOn": false,
        "placeholder": "请输入",
        "type": "text"
      },
      {
        "renderer": "divider",
        "text": "第三步",
        "icon": "Picture",
        "name": "three"
      },
      {
        "renderer": "input",
        "name": "address",
        "label": "住址",
        "tip": "请填写地址信息",
        "rules": [
          {
            "required": true,
            "message": "地址不能为空"
          },
          {
            "required": false
          }
        ],
        "hiddenOn": true
      }
    ],
    "processStatus": "wait",
    "finishStatus": "success",
    "simple": false,
    "activeIndex": 2
  }
}
