data.pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "cards",
      "virtual": true,
      "initApi": {
        "url": "/api/mock/columnData",
        "method": "get",
        "params": {}
      },
      "body": [
        {
          "renderer": "avatar",
          "title": "<%=data.id%>",
          "subTitle": "<%=data.province%>",
          "description": "<%=data.createdAt%>",
          "src": "https://fuss10.elemecdn.com/e/5d/4a731a90594a4af544c0c25941171jpeg.jpeg",
          "size": 70,
          "shape": "circle",
          "borderRadius": 0,
          "color": "#606266",
          "bgColor": "#ffffff",
          "body": [],
          "classname": "bounce_move_2"
        },
        {
          "renderer": "action",
          "text": "更新",
          "isText": true,
          "popupType": "tip",
          "tipContent": "asdfasdf",
          "size": "small",
          "actionType": "ajax",
          "actionApi": {
            "url": "/api/users?uid=${uid}",
            "method": "delete",
            "params": {}
          },
          "body": {}
        }
      ],
      "shadow": "hover",
      "span": 24,
      "bodyClass": "demo-list__each",
      "syncLocation": true
    }
  ],
  "innerStyle": ".demo-list__each {\n  position:relative;\n  padding:10px;\n  border-bottom:1px solid #EBEEF5;\n}\n.demo-list__each .i-action {\n  position:absolute;\n  right: 20px;\n  top:50%;\n  transform:translateY(-50%)\n}"
}
