function({_req}) {
  const taskId = _req.query.taskId;
  const id = _req.query.taskId;
  const taskName = "新手券" + _req.query.taskId;
  const couponNum = "40000" + _req.query.taskId;
  let sendNum;
  var max = "40000" + _req.query.taskId;
  var send = Number(_req.query.sendNum) + 30000;
  if (Number(_req.query.sendNum) < Number(max)) {
    sendNum = send;
  } else {
    sendNum = max;
  }
  let taskStatus;
  var round = Math.floor(Math.random() * (100 - 1)) + 1;
  if (_req.query.taskStatus == 2) {
      if (Number(_req.query.sendNum) < Number(max)) {
        taskStatus = 2;
      } else {
        taskStatus = 3;
      }
  }
  if (_req.query.taskStatus == 4) {
    taskStatus = round % 2 + 4;
  }
  return {
    id,
    taskId,
    taskName,
    taskStatus,
    couponNum,
    sendNum
  }
}
