function({_req}) {
    const type = _req.query.type || '';
    const curDatePrefix = _req.query.curDatePrefix || '';
    const datePrefix = curDatePrefix.split('-');
    const year = '2024';
    const m = new Date().getMonth();
    let month = datePrefix[1] || `${m + 1}`;
    if (month.startsWith('0')) {
      month = month.charAt(1);
    }
    const list = {};
    const timestamp = new Date();
    const date = timestamp.getDate();
    const days = new Date(year, month - 1, 0).getDate();
    const fullmonth = month < 10 ? `0${month}` : month;
    const Gan = new Array("甲", "乙", "丙", "丁", "戊", "己", "庚", "辛", "壬", "癸");
    const Zhi = new Array("子", "丑", "寅", "卯", "辰", "巳", "午", "未", "申", "酉", "戌", "亥");
    for (let i = 1; i < days + 1; i++) {
      let value;
      if (month == 1 && date >= 20 || month == 2 && date <= 18) {
        value = "水瓶座";
      }
      if (month == 1 && date > 31) {
        value = "Huh?";
      }
      if (month == 2 && date >= 19 || month == 3 && date <= 20) {
        value = "双鱼座";
      }
      if (month == 2 && date > 29) {
        value = "Say what?";
      }
      if (month == 3 && date >= 21 || month == 4 && date <= 19) {
        value = "白羊座";
      }
      if (month == 3 && date > 31) {
        value = "OK. Whatever.";
      }
      if (month == 4 && date >= 20 || month == 5 && date <= 20) {
        value = "金牛座";
      }
      if (month == 4 && date > 30) {
        value = "I'm soooo sorry!";
      }
      if (month == 5 && date >= 21 || month == 6 && date <= 21) {
        value = "双子座";
      }
      if (month == 5 && date > 31) {
        value = "Umm ... no.";
      }
      if (month == 6 && date >= 22 || month == 7 && date <= 22) {
        value = "巨蟹座";
      }
      if (month == 6 && date > 30) {
        value = "Sorry.";
      }
      if (month == 7 && date >= 23 || month == 8 && date <= 22) {
        value = "狮子座";
      }
      if (month == 7 && date > 31) {
        value = "Excuse me?";
      }
      if (month == 8 && date >= 23 || month == 9 && date <= 22) {
        value = "室女座";
      }
      if (month == 8 && date > 31) {
        value = "Yeah. Right.";
      }
      if (month == 9 && date >= 23 || month == 10 && date <= 22) {
        value = "天秤座";
      }
      if (month == 9 && date > 30) {
        value = "Try Again.";
      }
      if (month == 10 && date >= 23 || month == 11 && date <= 21) {
        value = "天蝎座";
      }
      if (month == 10 && date > 31) {
        value = "Forget it!";
      }
      if (month == 11 && date >= 22 || month == 12 && date <= 21) {
        value = "人马座";
      }
      if (month == 11 && date > 30) {
        value = "Invalid Date";
      }
      if (month == 12 && date >= 22 || month == 1 && date <= 19) {
        value = "摩羯座";
      }
      if (month == 12 && date > 31) {
        value = "No way!";
      }
      const hours = Math.floor(Math.random() * 24);
      const minutes = Math.floor(Math.random() * 60);  
      const seconds = Math.floor(Math.random() * 60);
      const fullday = i < 10 ? `0${i}` : i;
      list[`${year}-${fullmonth}-${fullday}`] = {
        status: Math.floor(Math.random() * 5),
        time: `${hours}:${minutes}:${seconds}`,
        value: type?'白班':`${(Gan[i%10]+Zhi[i%12])} ${value}`
      };
    }
    return list
}
