function({_req}) {
    const date = new Date(_req.query.calendar).getDate();
    const type = date%2===0? '白班（集团统一班次）': '夜班（集团统一班次）';
    const hour1 = Math.floor(Math.random() * 12);
    const hour2 = Math.floor(Math.random() * 12 + 12);

    return {
      type,
      mon: `上班：${hour1}:08`,
      eve: `下班：${hour2}:08`
    }
  }
