function({_req}) {
      const count = Number(_req.query.count) || 0;
      let index = count;
      if (!index) {
        index = 1;
      } else {
        index += 1;
      }
      const status1 = function() {
        if (index < 4) {
          return index % 4;
        } else {
          return 4;
        }
      };
      const status2 = function() {
        if (index >= 3 && index < 6) {
          return index % 3;
        }
        if (index == 6) {
          return 3;
        }
        if (index > 6) {
          return 4;
        } else {
          return 0;
        }
      };
      const status3 = function() {
        if (index >= 7) {
          return index % 6;
        }else if (index > 9) {
          return 4;
        } else {
          return 0;
        }
      };
      return { selectedRows: [{
        "label": "单台",
        "name": "hive",
        "status": status1(),
        "count": index
      }, {
        "label": "单边",
        "name": "partial",
        "status": status2(),
        "count": index
      }, {
        "label": "全量",
        "name": "full",
        "status": status3(),
        "count": index
      }]};
  }
