function({_req}) {
  const now = new Date();  
  const year = now.getFullYear();  
  const month = now.getMonth();  
  const monthlyDatesWeekdays = {};  
  for (let day = 1; day <= new Date(year, month + 1, 0).getDate(); day++) {  
    const date = new Date(year, month, day);  
    let dateStr = date.toISOString().split('T')[0].split('-')[2];  
    let weekdayStr = '';
    if (date.getDay() < 1) {
      weekdayStr = '周六'
    } else {
      weekdayStr = ['周日', '周一', '周二', '周三', '周四', '周五', '周六'][date.getDay() - 1]; 
    }
    dateStr = (parseInt(dateStr, 10) - 1).toString(); 
    monthlyDatesWeekdays[dateStr] = weekdayStr;  
  }  

  return monthlyDatesWeekdays;  
}
