pageSchema:
{
  "renderer": "page",
  "classname": "virus-page",
  "body": [
    {
      "renderer": "html",
      "html": "<h1>2022年12月1日至2023年3月23日全国新型冠状病毒感染疫情情况（MOCK数据）</h1>",
      "classname": "font-size-22 text-center margin-bottom-20"
    },
    {
      "renderer": "service",
      "initApi": {
        "url": "/api/mock/virus/country",
        "method": "get",
        "params": {}
      },
      "body": [
        {
          "renderer": "wrapper",
          "name": "PreviewDate",
          "bodyClassname": "preview-date swing_scale_1",
          "body": "<h1><%=data.name%></h1><div style=\"margin-bottom: 10px;\">current area</div><h1><%=$.update_time.$format()%></h1><div>time when data was last updated</div>",
          "footer": "<h1 style=\"color: #ffb600;\"><%=data.confirmed_count%></h1>\n<div>confirmed_count</div>\n<h1 style=\"color: blue;\"><%=data.suspected_count%></h1>\n<div>suspected_count</div>\n<h1 style=\"color: green;\"><%=data.cured_count%></h1>\n<div>cured_count</div>\n<h1 style=\"color: red;\"><%=data.dead_count%></h1>\n<div>dead_count</div>",
          "footerClassname": "preview-country swing_scale_1"
        }
      ]
    },
    {
      "renderer": "chart",
      "mapArea": "china",
      "initApi": {
        "url": "/api/mock/virus/province",
        "method": "get",
        "params": {
          "start_time": "${start_time}"
        }
      },
      "config": {
        "tooltip": {
          "trigger": "item"
        },
        "visualMap": {
          "min": 800,
          "max": 5000,
          "text": [
            "high",
            "low"
          ],
          "realtime": false,
          "calculable": true,
          "inRange": {
            "color": [
              "#e6f7ff",
              "#1890FF",
              "#0050b3"
            ]
          }
        },
        "series": [
          {
            "type": "map",
            "map": "china",
            "name": "人数统计",
            "data": "${list}"
          }
        ]
      },
      "height": 570,
      "target": "PreviewDate",
      "width": 490
    },
    {
      "renderer": "divider",
      "text": "省市地区"
    },
    {
      "renderer": "chart",
      "name": "HKMap",
      "height": 626,
      "mapArea": "HK",
      "config": {
        "title": {
          "text": "Population Density of Hong Kong （2011）",
          "subtext": "Data from Wikipedia"
        },
        "tooltip": {
          "trigger": "item",
          "formatter": "{b}<br/>{c} (p / km2)"
        },
        "toolbox": {
          "show": true,
          "orient": "vertical",
          "left": "right",
          "top": "center",
          "feature": {
            "dataView": {
              "readOnly": false
            },
            "restore": {},
            "saveAsImage": {}
          }
        },
        "visualMap": {
          "min": 800,
          "max": 50000,
          "text": [
            "High",
            "Low"
          ],
          "realtime": false,
          "calculable": true,
          "inRange": {
            "color": [
              "lightskyblue",
              "yellow",
              "orangered"
            ]
          }
        },
        "series": [
          {
            "name": "香港18区人口密度",
            "type": "map",
            "map": "HK",
            "label": {
              "show": true
            },
            "data": [
              {
                "name": "中西区",
                "value": 20057.34
              },
              {
                "name": "湾仔",
                "value": 15477.48
              },
              {
                "name": "东区",
                "value": 31686.1
              },
              {
                "name": "南区",
                "value": 6992.6
              },
              {
                "name": "油尖旺",
                "value": 44045.49
              },
              {
                "name": "深水埗",
                "value": 40689.64
              },
              {
                "name": "九龙城",
                "value": 37659.78
              },
              {
                "name": "黄大仙",
                "value": 45180.97
              },
              {
                "name": "观塘",
                "value": 55204.26
              },
              {
                "name": "葵青",
                "value": 21900.9
              },
              {
                "name": "荃湾",
                "value": 4918.26
              },
              {
                "name": "屯门",
                "value": 5881.84
              },
              {
                "name": "元朗",
                "value": 4178.01
              },
              {
                "name": "北区",
                "value": 2227.92
              },
              {
                "name": "大埔",
                "value": 2180.98
              },
              {
                "name": "沙田",
                "value": 9172.94
              },
              {
                "name": "西贡",
                "value": 3368
              },
              {
                "name": "离岛",
                "value": 806.98
              }
            ],
            "nameMap": {
              "Central and Western": "中西区",
              "Eastern": "东区",
              "Islands": "离岛",
              "Kowloon City": "九龙城",
              "Kwai Tsing": "葵青",
              "Kwun Tong": "观塘",
              "North": "北区",
              "Sai Kung": "西贡",
              "Sha Tin": "沙田",
              "Sham Shui Po": "深水埗",
              "Southern": "南区",
              "Tai Po": "大埔",
              "Tsuen Wan": "荃湾",
              "Tuen Mun": "屯门",
              "Wan Chai": "湾仔",
              "Wong Tai Sin": "黄大仙",
              "Yau Tsim Mong": "油尖旺",
              "Yuen Long": "元朗"
            }
          }
        ]
      }
    }
  ],
  "innerStyle": ".virus-page {\n  position: relative;\n  h1 {\n    margin: 0;\n  }\n}\n.preview-date {\n  position: absolute;\n  top: 100px;\n  right: 20px;\n  width: 200px;\n  height: 144px;\n  padding: 10px;\n  margin-bottom: 3rem;\n  border-radius: 6px;\n  color: #474747;\n  border: 1px solid #cccccc;\n  backdrop-filter: blur(5px);\n  background-color: rgba(255, 255, 255, .8);  \n}\n.preview-country {\n  position: absolute;\n  top: 260px;\n  right: 20px;\n  width: 200px;\n  padding: 10px;\n  margin-bottom: 3rem;\n  border-radius: 6px;\n  color: #474747;\n  border: 1px solid #cccccc;\n  backdrop-filter: blur(5px);\n  background-color: rgba(255, 255, 255, .8);\n}"
}
