data.pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "service",
      "initApi": {
        "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/scheduleTable",
        "method": "get",
        "params": {},
        "cached": true
      },
      "body": [
        {
          "renderer": "render",
          "classname": "schedule-table__header",
          "body": [
            {
              "renderer": "span",
              "innerHTML": "资源详情",
              "style": {
                "display": "inline-block",
                "width": "140px"
              }
            },
            {
              "renderer": "span",
              "innerHTML": "计划详情",
              "style": {
                "display": "inline-block",
                "width": "140px"
              }
            },
            {
              "renderer": "span",
              "innerHTML": "创意详情",
              "style": {
                "display": "inline-block",
                "width": "140px"
              }
            },
            {
              "renderer": "mapping",
              "name": "global_delivery_dates",
              "body": {
                "renderer": "each"
              }
            }
          ]
        }
      ],
      "silentLoading": true
    },
    {
      "renderer": "table",
      "initApi": {
        "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/scheduleTable",
        "method": "get",
        "params": {},
        "cached": true
      },
      "showHeader": false,
      "pageConfig": {
        "pageIndex": "pageIndex",
        "pageSize": "pageSize",
        "total": "total",
        "rows": "rows"
      },
      "columns": [
        {
          "label": "资源详情",
          "name": "resource_id",
          "fixed": false,
          "sortable": false,
          "width": "140px",
          "type": "",
          "align": "",
          "body": [
            {
              "renderer": "render",
              "body": [
                {
                  "innerHTML": "<%=data.resource_name%>",
                  "renderer": "div",
                  "style": {
                    "color": "#525a64",
                    "font-size": "12px"
                  }
                }
              ]
            }
          ]
        },
        {
          "label": "计划详情",
          "name": "campaign_id",
          "fixed": false,
          "sortable": false,
          "width": "140px",
          "type": "",
          "align": "",
          "body": [
            {
              "renderer": "render",
              "body": [
                {
                  "innerHTML": "<%=data.campaign_name%>",
                  "renderer": "div",
                  "style": {
                    "color": "#525a64",
                    "font-size": "12px"
                  }
                },
                {
                  "innerHTML": "<%=data.campaign_delivery_dates%>",
                  "renderer": "div",
                  "style": {
                    "color": "#9ca1a7"
                  }
                },
                {
                  "renderer": "mapping",
                  "name": "campaign_status",
                  "map": {
                    "0": "待投",
                    "1": "在投",
                    "2": "暂停",
                    "3": "结束",
                    "4": "待审",
                    "5": "驳回"
                  }
                }
              ]
            }
          ]
        },
        {
          "label": "创意详情",
          "name": "creative_name",
          "fixed": false,
          "sortable": false,
          "width": "140px",
          "type": "",
          "align": "",
          "body": [
            {
              "renderer": "render",
              "body": [
                {
                  "innerHTML": "<%=data.creative_name%>",
                  "renderer": "div",
                  "style": {
                    "color": "#525a64",
                    "font-size": "12px"
                  }
                },
                {
                  "innerHTML": "<%=data.delivery_dates%>",
                  "renderer": "div",
                  "style": {
                    "color": "#9ca1a7"
                  }
                }
              ]
            }
          ]
        },
        {
          "label": "排期",
          "name": "delivery_dates",
          "fixed": false,
          "sortable": false,
          "type": "",
          "align": "",
          "body": [
            {
              "renderer": "wrapper",
              "classname": "delivery_date__wrapper zoom_3",
              "computedClass": "'indent-'+data.delivery_indent+' length-'+data.delivery_length",
              "body": [
                {
                  "renderer": "render",
                  "body": [
                    {
                      "renderer": "mapping",
                      "name": "creative_status",
                      "map": {
                        "0": "草稿",
                        "1": "待审",
                        "2": "通过",
                        "3": "驳回",
                        "4": "结束",
                        "5": "删除",
                        "6": "失效",
                        "7": "同步中",
                        "8": "转码失败",
                        "-1": "待传"
                      },
                      "action": {
                        "renderer": "action",
                        "text": "",
                        "size": "mini",
                        "actionType": "url",
                        "url": "https://www.baidu.com?name=${creative_id}"
                      }
                    }
                  ]
                }
              ]
            }
          ]
        }
      ],
      "actions": [],
      "mergeCell": true
    }
  ],
  "innerStyle": ".schedule-table__header {\n    display: flex;\n    height: 46px;\n    padding: 0 10px;\n    line-height: 46px;\n    border-top: 1px solid #e6e7e8;\n    border-right: 1px solid #e6e7e8;\n    border-left: 1px solid #e6e7e8;\n    font-weight: 500;\n    color: #39434e;\n    .i-mapping__container,\n    .i-each__container__item {\n        flex: 1;\n    }\n    .i-each__container {\n        display: flex;\n    }\n}\n.delivery_date__wrapper {\n    height: 32px;\n    padding: 0 10px;\n    line-height: 32px;\n    cursor: pointer;\n    border-radius: 2px;\n    color: #436eee;\n    background: #e8edfd;\n}\n.indent-1 {\n    margin-left: 10%;\n}\n.indent-2 {\n    margin-left: 20%;\n}\n.indent-3 {\n    margin-left: 30%;\n}\n.indent-4 {\n    margin-left: 40%;\n}\n.indent-5 {\n    margin-left: 50%;\n}\n.indent-6 {\n    margin-left: 60%;\n}\n.indent-7 {\n    margin-left: 70%;\n}\n.length-1 {\n    width: 10%;\n}\n.length-2 {\n    width: 20%;\n}\n.length-3 {\n    width: 30%;\n}\n.length-4 {\n    width: 40%;\n}\n.length-5 {\n    width: 50%;\n}\n.length-6 {\n    width: 60%;\n}\n.length-7 {\n    width: 70%;\n}"
}
