data.pageSchema:
{
  "renderer": "page",
  "initData": {
    "name": "",
    "syncValid": 123,
    "sex": "0",
    "role": [],
    "connect": "0",
    "mail": "fasdgre@jl.com",
    "phone": "4665432465",
    "createdAt": "",
    "adult": true,
    "color": "#456533",
    "rate": 5,
    "textarea": "234",
    "richtxt": "eee"
  },
  "body": [
    {
      "renderer": "form",
      "size": "small",
      "target": "UserTable",
      "actionApi": {
        "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/form/submit",
        "params": {},
        "method": "post"
      },
      "controls": [
        {
          "renderer": "divider",
          "label": "1"
        },
        {
          "renderer": "input",
          "name": "name",
          "label": "姓名",
          "tip": "请填写姓名",
          "placeholder": "请填写姓名",
          "requiredOn": true,
          "type": "text",
          "validType": "normal"
        },
        {
          "renderer": "input",
          "name": "asyncValid",
          "label": "异步校验",
          "tip": "异步校验",
          "placeholder": "异步校验",
          "type": "number",
          "validType": "async",
          "rules": [
            {
              "exp": {
                "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/form/verify",
                "method": "get",
                "params": {
                  "asyncValid": ""
                }
              },
              "trigger": "blur"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "sex",
          "label": "性别",
          "tip": "性别",
          "defaultValue": "0",
          "options": [
            {
              "value": "1",
              "text": "男"
            },
            {
              "value": "0",
              "text": "女"
            }
          ]
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 1",
          "options": [
            {
              "value": "1",
              "text": "爷爷"
            },
            {
              "value": "2",
              "text": "大伯"
            },
            {
              "value": "3",
              "text": "叔叔"
            },
            {
              "value": "4",
              "text": "哥哥"
            },
            {
              "value": "5",
              "text": "弟弟"
            }
          ],
          "multiple": true,
          "optionConfig": {
            "text": "text",
            "value": "value"
          }
        },
        {
          "renderer": "divider",
          "label": "2"
        },
        {
          "renderer": "select",
          "name": "role",
          "label": "角色",
          "tip": "请选择角色",
          "visibleOn": "data.sex == 0",
          "options": [
            {
              "value": "6",
              "text": "奶奶"
            },
            {
              "value": "7",
              "text": "大姑"
            },
            {
              "value": "8",
              "text": "小姑"
            },
            {
              "value": "9",
              "text": "姐姐"
            },
            {
              "value": "0",
              "text": "妹妹"
            }
          ]
        },
        {
          "renderer": "radio",
          "name": "connect",
          "label": "联系方式",
          "tip": "联系方式",
          "defaultValue": "0",
          "options": [
            {
              "value": "0",
              "text": "电话"
            },
            {
              "value": "1",
              "text": "邮箱"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "mail",
          "label": "邮件",
          "tip": "填写邮箱",
          "visibleOn": "data.connect == 1",
          "placeholder": "请填写邮箱"
        },
        {
          "renderer": "input",
          "name": "phone",
          "label": "电话",
          "tip": "预留电话",
          "visibleOn": "data.connect == 0",
          "placeholder": "请填写电话"
        },
        {
          "renderer": "datepicker",
          "name": "createdAt",
          "tip": "请选择派送日期",
          "type": "datetime",
          "readonly": false,
          "disabledOn": "data.name=='shuzhong'",
          "format": "YYYY-MM-DD",
          "label": "日期1"
        },
        {
          "renderer": "switch",
          "name": "adult",
          "label": "成年",
          "defaultValue": true,
          "tip": "已满十八岁"
        },
        {
          "renderer": "colorpicker",
          "name": "color",
          "label": "颜色",
          "rules": [
            {
              "required": true,
              "message": "评论不能为空"
            }
          ]
        },
        {
          "renderer": "rate",
          "name": "rate",
          "label": "评论",
          "rules": [
            {
              "required": true,
              "message": "评论不能为空"
            }
          ]
        },
        {
          "renderer": "input",
          "name": "textarea",
          "label": "文本框",
          "placeholder": "占位符",
          "type": "textarea",
          "validType": "normal",
          "rules": [
            {
              "required": false
            }
          ]
        },
        {
          "renderer": "action",
          "actionType": "submit",
          "text": "提交",
          "initApi": {
            "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/calendar",
            "method": "get",
            "params": {}
          },
          "silentMessage": true,
          "silentLoading": false,
          "cached": false,
          "syncLocation": false,
          "plain": true,
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {}
        },
        {
          "renderer": "action",
          "text": "重置",
          "type": "warning",
          "plain": true,
          "actionType": "reset",
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {},
          "popupType": "confirm",
          "confirmTitle": "确定要重置吗？"
        }
      ],
      "inline": true
    },
    {
      "renderer": "table",
      "name": "UserTable",
      "showExport": true,
      "height": 500,
      "initApi": {
        "url": "/api/users",
        "method": "get",
        "params": {
          "timestamp": "",
          "name": "${name}",
          "createdAt": "${createdAt}",
          "role": "${role}"
        }
      },
      "selectableOn": "data.name!='1345'",
      "columns": [
        {
          "type": "index",
          "body": [],
          "align": "none",
          "fixed": "left"
        },
        {
          "label": "uid",
          "name": "uid",
          "body": [
            {
              "renderer": "action",
              "text": "<%=$.id%>",
              "isText": true,
              "actionType": "url",
              "url": "/demo/virtuleList?pageIndex=11&pageSize=10"
            }
          ],
          "align": "center",
          "visibleOn": "data.type!=1",
          "fixed": "left",
          "type": "-"
        },
        {
          "name": "name",
          "label": " 姓名",
          "body": [
            {
              "renderer": "quickedit",
              "label": "<%=data.name%>",
              "keyword": "name",
              "rules": [
                {
                  "exp": "!/^[a-zA-Z0-9_\\u4e00-\\u9fa5]+$/.test(data.name)",
                  "message": "必须是汉字、数字、字母、下划线",
                  "trigger": "blur"
                }
              ],
              "actionApi": {
                "url": "/api/user",
                "method": "post",
                "params": {
                  "name": "${name}",
                  "uid": "${uid}"
                }
              },
              "target": "UserTable",
              "title": "更新姓名",
              "reload": "UserTable"
            }
          ],
          "align": "right",
          "sortable": true
        },
        {
          "fixed": false,
          "sortable": false,
          "type": "none",
          "align": "center",
          "body": [
            {
              "renderer": "mapping",
              "value": "sex",
              "map": {
                "0": "女",
                "1": "男"
              }
            }
          ]
        },
        {
          "label": "创建时间",
          "name": "createdAt",
          "fixed": false,
          "sortable": false,
          "type": "",
          "align": "center",
          "body": []
        },
        {
          "label": "更新时间",
          "name": "updatedAt",
          "fixed": false,
          "sortable": false,
          "type": "",
          "align": "center",
          "body": []
        },
        {
          "label": "操作",
          "name": "id",
          "body": [
            {
              "renderer": "action",
              "name": "UpdateAction",
              "text": "更新",
              "type": "success",
              "actionType": "dialog",
              "plain": true,
              "size": "small",
              "group": true,
              "body": {
                "direction": "ltr",
                "closeOnClickModal": true,
                "closeOnPressEscape": true,
                "footer": [
                  {
                    "renderer": "action",
                    "text": "更新",
                    "actionType": "submit",
                    "actionApi": {
                      "url": "/api/demo",
                      "method": "post",
                      "params": {}
                    },
                    "remoteComponent": "UpdateForm"
                  }
                ],
                "body": [
                  {
                    "renderer": "form",
                    "name": "UpdateForm",
                    "actionApi": {
                      "url": "/api/user?uid=${uid}",
                      "method": "put",
                      "params": {
                        "*": "*"
                      }
                    },
                    "controls": [
                      {
                        "renderer": "input",
                        "name": "name",
                        "label": "姓名",
                        "placeholder": "占位符",
                        "type": "text",
                        "validType": "normal"
                      }
                    ],
                    "reload": "UserTable"
                  }
                ],
                "fullscreen": false,
                "center": false,
                "autoNext": true,
                "draggable": false
              },
              "actionApi": {
                "url": "",
                "method": "post",
                "params": {}
              }
            },
            {
              "renderer": "action",
              "size": "small",
              "group": true,
              "actionType": "drawer",
              "actionApi": {
                "url": "",
                "method": "post",
                "params": {}
              },
              "body": {
                "title": "模态框",
                "closeOnClickModal": true,
                "footer": [],
                "body": [
                  {
                    "renderer": "service",
                    "name": "ChartService",
                    "initApi": {
                      "url": "https://www.fastmock.site/mock/a93e0b29161761b8153cbc02db04c643/api/chart/map",
                      "method": "get",
                      "params": {
                        "start_time": ""
                      }
                    },
                    "body": {
                      "renderer": "chart",
                      "mapArea": "china",
                      "config": {
                        "tooltip": {
                          "trigger": "item"
                        },
                        "visualMap": {
                          "min": 800,
                          "max": 5000,
                          "text": [
                            "high",
                            "low"
                          ],
                          "realtime": false,
                          "calculable": true,
                          "inRange": {
                            "color": [
                              "#e6f7ff",
                              "#1890FF",
                              "#0050b3"
                            ]
                          }
                        },
                        "series": [
                          {
                            "type": "map",
                            "map": "china",
                            "name": "人均收入",
                            "data": "${list}"
                          }
                        ]
                      },
                      "height": 500,
                      "initData": {
                        "start_time": ""
                      }
                    }
                  }
                ]
              },
              "text": "查看",
              "plain": true,
              "params": {}
            },
            {
              "renderer": "action",
              "text": "删除",
              "group": true,
              "type": "warning",
              "plain": true,
              "size": "small",
              "popupType": "confirm",
              "confirmTitle": "确定删除吗？",
              "actionType": "ajax",
              "actionApi": {
                "url": "/api/user?uid=",
                "method": "delete",
                "params": {}
              },
              "reload": "UserTable",
              "params": {},
              "body": {}
            }
          ]
        }
      ],
      "actions": [
        {
          "renderer": "Extable",
          "lazy": true,
          "name": "extable",
          "target": "UserTable"
        },
        {
          "renderer": "action",
          "text": "批量删除",
          "size": "small",
          "actionType": "ajax",
          "actionApi": {
            "url": "/api/users",
            "method": "delete",
            "params": {
              "*": "*"
            }
          },
          "params": {},
          "body": {},
          "plain": true,
          "disabledOn": "!data.selectedRows.length",
          "icon": "Delete"
        },
        {
          "renderer": "action",
          "size": "small",
          "icon": "Printer",
          "actionType": "print",
          "actionApi": {
            "url": "",
            "method": "post",
            "params": {}
          },
          "params": {},
          "body": {},
          "text": "打印",
          "plain": true,
          "target": "UserTable"
        }
      ],
      "updateOnRowId": "uid",
      "showDynamicColumn": true,
      "pageConfig": {
        "pageIndex": "pageIndex",
        "pageSize": "pageSize",
        "total": "count",
        "rows": "rows",
        "iLayout": [
          "prev",
          "pager",
          "next",
          "total"
        ]
      },
      "reactive": true
    }
  ],
  "steps": []
}
