data.pageSchema:
{
  "renderer": "page",
  "initData": {
    "timestamp": ""
  },
  "body": [
    {
      "renderer": "image",
      "src": "https://songshuzhong.github.io/i-assets/public/img/h5/shamo.jpg",
      "classname": "shopping-page__bg",
      "previews": []
    },
    {
      "renderer": "form",
      "controls": [
        {
          "renderer": "Bridge",
          "name": "suit_info",
          "library": "WeiboJSBridge",
          "type": "invoke",
          "method": "GetLightSkinInfo"
        },
        {
          "renderer": "computed",
          "name": "in_app",
          "condition": "data.suit_info",
          "exp": "data.suit_info.status"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "skeleton",
              "hasAvatar": true,
              "width": 70,
              "height": 12,
              "count": 2,
              "variant": "circle",
              "loadingOn": "!data.timestamp",
              "body": [
                {
                  "renderer": "avatar",
                  "src": "${home_src}",
                  "title": "<%=data.home_title%>",
                  "subTitle": "<%=data.home_desc%>",
                  "isComputedSrc": true,
                  "shape": "square",
                  "classname": "shopping-logo"
                },
                {
                  "renderer": "switch",
                  "name": "home_status",
                  "groupName": "by_checked_all",
                  "inlinePrompt": false,
                  "disabledOn": "data.in_app == 0",
                  "type": "checkbox",
                  "overForm": true,
                  "rules": [],
                  "target": "shoppingCar",
                  "activeText": "",
                  "inactiveText": ""
                }
              ]
            }
          ],
          "classname": "shopping-card"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "skeleton",
              "hasAvatar": true,
              "width": 70,
              "height": 12,
              "count": 2,
              "variant": "circle",
              "loadingOn": "!data.timestamp",
              "body": [
                {
                  "renderer": "avatar",
                  "src": "${icon_src}",
                  "title": "<%=data.icon_title%>",
                  "subTitle": "<%=data.icon_desc%>",
                  "isComputedSrc": true,
                  "shape": "square",
                  "classname": "shopping-logo"
                },
                {
                  "renderer": "switch",
                  "name": "icon_status",
                  "groupName": "by_checked_all",
                  "inlinePrompt": false,
                  "type": "checkbox",
                  "rules": [],
                  "target": "shoppingCar",
                  "activeText": "",
                  "inactiveText": ""
                }
              ]
            }
          ],
          "classname": "shopping-card"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "skeleton",
              "hasAvatar": true,
              "width": 70,
              "height": 12,
              "count": 2,
              "variant": "circle",
              "loadingOn": "!data.timestamp",
              "body": [
                {
                  "renderer": "avatar",
                  "src": "${bubble_src}",
                  "title": "<%=data.bubble_title%>",
                  "subTitle": "<%=data.bubble_desc%>",
                  "isComputedSrc": true,
                  "shape": "square",
                  "classname": "shopping-logo"
                },
                {
                  "renderer": "switch",
                  "name": "bubble_status",
                  "groupName": "by_checked_all",
                  "inlinePrompt": false,
                  "type": "checkbox",
                  "rules": [],
                  "target": "shoppingCar",
                  "activeText": "",
                  "inactiveText": ""
                }
              ]
            }
          ],
          "classname": "shopping-card"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "skeleton",
              "hasAvatar": true,
              "width": 70,
              "height": 12,
              "count": 2,
              "variant": "circle",
              "loadingOn": "!data.timestamp",
              "body": [
                {
                  "renderer": "avatar",
                  "src": "${thumb_src}",
                  "title": "<%=data.thumb_title%>",
                  "subTitle": "<%=data.thumb_desc%>",
                  "isComputedSrc": true,
                  "shape": "square",
                  "classname": "shopping-logo"
                },
                {
                  "renderer": "switch",
                  "name": "thumb_status",
                  "groupName": "by_checked_all",
                  "inlinePrompt": false,
                  "type": "checkbox",
                  "rules": [],
                  "target": "shoppingCar",
                  "activeText": "",
                  "inactiveText": ""
                }
              ]
            }
          ],
          "classname": "shopping-card"
        },
        {
          "renderer": "wrapper",
          "header": [
            {
              "renderer": "html",
              "classname": "mobile-tip",
              "visibleOn": "data.in_app == 0",
              "html": "《三国演义》app外不可选！"
            }
          ],
          "body": [
            {
              "renderer": "switch",
              "name": "checked_all",
              "inlinePrompt": false,
              "type": "checkbox",
              "rules": [],
              "target": "shoppingCar",
              "triggered": "by_checked_all",
              "activeText": "全选",
              "inactiveText": "全选"
            },
            {
              "renderer": "switch",
              "name": "share",
              "inlinePrompt": false,
              "type": "checkbox",
              "rules": [],
              "target": "shoppingCar",
              "activeText": "分享到微博",
              "inactiveText": "分享到微博"
            },
            {
              "renderer": "action",
              "text": "一键佩戴",
              "actionType": "submit",
              "round": true,
              "triggered": "shoppingCar",
              "disabledOn": "!data.home_status&&!data.icon_status&&!data.bubble_status&&!data.thumb_status"
            }
          ],
          "bodyClassname": "shopping-footer"
        }
      ],
      "name": "shoppingCar",
      "initData": {
        "checked_all": false
      },
      "initApi": {
        "url": "",
        "method": "get",
        "params": {}
      },
      "actionApi": {
        "url": "/api/submit",
        "method": "post",
        "params": {}
      },
      "classname": "shopping-form"
    },
    {
      "renderer": "service",
      "target": "shoppingCar",
      "initApi": {
        "url": "/api/mock/iconHome?type=icon",
        "method": "get",
        "params": {}
      },
      "silentLoading": true
    },
    {
      "renderer": "service",
      "target": "shoppingCar",
      "initApi": {
        "url": "/api/mock/iconHome?type=home",
        "method": "get",
        "params": {}
      },
      "silentLoading": true
    },
    {
      "renderer": "service",
      "target": "shoppingCar",
      "initApi": {
        "url": "/api/mock/iconHome?type=bubble",
        "method": "get",
        "params": {}
      },
      "silentLoading": true
    },
    {
      "renderer": "service",
      "target": "shoppingCar",
      "initApi": {
        "url": "/api/mock/iconHome?type=thumb",
        "method": "get",
        "params": {}
      },
      "silentLoading": true
    }
  ],
  "innerStyle": ".shopping-page {\n  position: relative;\n  width: 375px;\n  margin: auto;\n  padding-top: 223px;\n  overflow-y: scroll;\n}\n.shopping-page__bg {\n  position: absolute;\n  top: 0;\n  left: 0;\n  width: 375px;\n  height: 320px;\n}\n.mobile-tip {\n  display: block;\n  text-align: center;\n  color: var(--el-color-danger);\n  font-weight: 800;\n}\n.shopping-footer {\n  padding: 10px 0;\n  text-align: center;\n  .el-checkbox {\n    margin-right: 10px;\n  }\n  .el-button {\n    width: 150px;\n    transform: translate(0px, -6px);\n  }\n}\n.el-form-item.i-form__wrapper {\n  margin-bottom: 0;\n}\n.shopping-card {\n  width: 331px;\n  display: block;\n  margin: 10px auto;\n  border-radius: 14px;\n  .i-card__body {\n    padding: 8px;\n    border-bottom: 0;\n  }\n  .el-checkbox {\n    position: absolute;\n    top: 50%;\n    right: 0;\n    transform: translateY(-50%);\n  }\n  .el-checkbox__inner {\n    width: 24px;\n    height: 24px;  \n    border-radius: 100%;\n    &::after {\n      top: 4px;\n      left: 9px;\n      width: 5px;\n      height: 10px;\n    }\n  }\n}\n.shopping-logo {\n  margin-top: 5px;\n}",
  "classname": "shopping-page"
}
