data.pageSchema:
{
  "renderer": "page",
  "body": {
    "renderer": "cards",
    "name": "cards1",
    "initApi": {
      "url": "/api/mock/products",
      "method": "get",
      "params": {}
    },
    "gutter": 30,
    "bodyStyle": {
      "padding": "0"
    },
    "classname": "card-margin",
    "header": [],
    "body": [
      {
        "renderer": "image",
        "computedSrc": true,
        "src": "${cover}",
        "classname": "zoom_1",
        "previews": []
      },
      {
        "renderer": "mapping",
        "name": "status",
        "status": "status",
        "optionConfig": {
          "text": "text",
          "value": "value"
        },
        "map": {
          "2": "推荐",
          "4": "缺货"
        }
      }
    ],
    "footer": [
      {
        "renderer": "html",
        "html": "<%=data.name%>",
        "classname": "font-weight-600 text-center zoom_3"
      },
      {
        "renderer": "html",
        "html": "<%=data.desc%>",
        "classname": "font-size-12 text-center margin-10 back_move_3"
      },
      {
        "renderer": "html",
        "html": "￥<%=data.price%>",
        "classname": "text-center price shake_3"
      }
    ],
    "span": 6,
    "pageConfig": {
      "pageIndex": "pageIndex",
      "pageSize": "pageSize",
      "total": "total",
      "rows": "rows"
    },
    "actions": [
      {
        "renderer": "action"
      }
    ],
    "hasPageInfo": false,
    "shadow": "hover",
    "skeletons": 6
  },
  "classname": "demo-page__products",
  "innerStyle": ".demo-page__products {\n    img {\n        width: 173px;\n        height: 173px;\n    }\n    .price {\n        color: rgb(255, 103, 0);\n    }\n    .i-card__body {\n        position: relative;\n    }\n    .i-mapping__container {\n      &.primary {\n        background: var(--el-color-primary);\n      }\n      &.success {\n        background: var(--el-color-success);\n      }\n      &.danger {\n        background: var(--el-color-danger);\n      }\n      &.warning {\n        background: var(--el-color-warning);\n      }\n      position: absolute;\n      top: 5px;\n      right: -27px;\n      width: 100px;\n      padding: 6px;\n      text-align: center;\n      color: white;\n      transform: rotateZ(45deg);\n      box-shadow: var(--el-box-shadow-dark);\n      .el-badge {\n          display: none;\n      }\n    }\n}",
  "steps": []
}
