data.pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "table",
      "height": 460,
      "updateOnRowId": "name",
      "columns": [
        {
          "type": "selection",
          "body": [],
          "fixed": false,
          "align": "none",
          "label": "",
          "name": ""
        },
        {
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [
            {
              "renderer": "html",
              "html": "<%=data.label%>",
              "inline": true
            }
          ],
          "label": "进度",
          "name": "name"
        },
        {
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [
            {
              "renderer": "mapping",
              "value": "status",
              "optionConfig": {},
              "map": {
                "0": "待启动",
                "1": "启动中",
                "2": "部署中",
                "3": "上线中",
                "4": "已上线"
              },
              "status": "status"
            }
          ],
          "label": "状态",
          "name": "status"
        },
        {
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [],
          "label": "机器",
          "name": "status"
        },
        {
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [
            {
              "renderer": "progress",
              "exp": "data.status * 25",
              "color": []
            }
          ],
          "label": "进度",
          "name": "status"
        },
        {
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [
            {
              "renderer": "action",
              "text": "暂停",
              "size": "small",
              "body": {},
              "disabledOn": "data.status==0||data.status==4"
            },
            {
              "renderer": "action",
              "text": "预览",
              "size": "small",
              "body": {},
              "disabledOn": "data.status!=4"
            },
            {
              "renderer": "service",
              "sendOn": "data.status==1 || data.status ==2 || data.status==3",
              "initApi": {
                "url": "/api/mock/tasks?count=${count}",
                "method": "post",
                "params": {
                  "*": "*"
                }
              },
              "stopAutoRefreshWhen": "data.status==3",
              "interval": 2000,
              "linkedOn": "data.name == target.name",
              "timeout": 10000,
              "reload": "Tasks2Table",
              "silentLoading": true
            }
          ],
          "label": "操作"
        }
      ],
      "actions": [
        {
          "renderer": "action",
          "text": "上线",
          "size": "small",
          "actionType": "ajax",
          "actionApi": {
            "url": "/api/mock/tasks",
            "method": "post",
            "params": {}
          },
          "body": {},
          "reload": "Tasks2Table"
        }
      ],
      "initApi": {
        "url": "/api/mock/tasks2",
        "method": "get",
        "params": {}
      },
      "name": "Tasks2Table",
      "showDynamicColumn": true,
      "title": "部署上线",
      "pageConfig": {
        "pageIndex": "pageIndex",
        "pageSize": "pageSize",
        "total": "total",
        "rows": "rows",
        "iLayout": [
          "prev",
          "pager",
          "next",
          "jumper",
          "sizes",
          "total"
        ]
      },
      "hasPageInfo": false,
      "reactive": true,
      "highlightCurrentRow": false,
      "rowKey": "label"
    }
  ]
}
