pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "service",
      "initApi": {
        "url": "/api/mock/pv",
        "method": "get",
        "params": {}
      },
      "interval": 10000,
      "body": [
        {
          "renderer": "grid",
          "span": 6,
          "gutter": 4,
          "body": [
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "avatar",
                  "title": "访问量",
                  "subTitle": "<%=data.pv%>",
                  "description": "每10秒钟更新一次",
                  "icon": "User",
                  "size": 70,
                  "shape": "circle",
                  "borderRadius": 10,
                  "invert": true,
                  "color": "#02FCF4",
                  "bgColor": "#ffffff"
                }
              ]
            },
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "avatar",
                  "title": "用户反馈",
                  "subTitle": "<%=data.feedback%>",
                  "description": "每10秒钟更新一次",
                  "icon": "Message",
                  "size": 70,
                  "shape": "circle",
                  "borderRadius": 10,
                  "invert": true,
                  "color": "#0455F7",
                  "bgColor": "#ffffff"
                }
              ]
            },
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "avatar",
                  "title": "赞助数",
                  "subTitle": "<%=data.support%>",
                  "description": "每10秒钟更新一次",
                  "icon": "Money",
                  "size": 70,
                  "shape": "circle",
                  "borderRadius": 10,
                  "invert": true,
                  "color": "#F76104",
                  "bgColor": "#ffffff"
                }
              ]
            },
            {
              "renderer": "card",
              "body": [
                {
                  "renderer": "avatar",
                  "title": "授权数",
                  "subTitle": "<%=data.fork%>",
                  "description": "授权数量",
                  "icon": "ShoppingCartFull",
                  "size": 70,
                  "shape": "circle",
                  "borderRadius": 10,
                  "invert": true,
                  "color": "#85F10A",
                  "bgColor": "#ffffff"
                }
              ]
            }
          ]
        }
      ],
      "timeout": 0,
      "silentLoading": true,
      "classname": "move_1"
    },
    {
      "renderer": "grid",
      "span": 12,
      "gutter": 4,
      "body": [
        {
          "renderer": "card",
          "body": {
            "renderer": "service",
            "initApi": {
              "url": "/api/mock/chart/love",
              "method": "get",
              "params": {}
            },
            "body": {
              "renderer": "chart",
              "config": {
                "title": {
                  "text": "${sayHi}"
                },
                "tooltip": {
                  "trigger": "axis",
                  "axisPointer": {
                    "type": "cross"
                  }
                },
                "polar": {},
                "angleAxis": {
                  "type": "value",
                  "startAngle": 0
                },
                "radiusAxis": {},
                "series": [
                  {
                    "coordinateSystem": "polar",
                    "name": "line",
                    "type": "line",
                    "data": "${list}"
                  }
                ]
              },
              "height": 302
            }
          },
          "classname": "move_4"
        },
        {
          "renderer": "card",
          "body": [
            {
              "renderer": "service",
              "name": "",
              "stopAutoRefreshWhen": "",
              "timeout": 0,
              "reload": "",
              "classname": "",
              "initApi": {
                "url": "/api/mock/dependencies",
                "method": "get",
                "params": {}
              },
              "footer": [],
              "body": [
                {
                  "renderer": "static",
                  "columns": [
                    {
                      "label": "vue",
                      "name": "vue",
                      "fixed": false,
                      "sortable": false,
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "vue-router",
                      "name": "vue-router",
                      "fixed": false,
                      "sortable": false,
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "axios",
                      "name": "axios",
                      "fixed": false,
                      "sortable": false,
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "element-plus",
                      "name": "element-plus",
                      "fixed": "",
                      "sortable": "",
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "@element-plus/icons-vue",
                      "name": "@element-plus/icons-vue",
                      "fixed": "",
                      "sortable": "",
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "monaco-editor",
                      "name": "monaco-editor",
                      "fixed": false,
                      "sortable": false,
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "echarts",
                      "name": "echarts",
                      "fixed": false,
                      "sortable": false,
                      "type": "",
                      "align": "left",
                      "body": []
                    },
                    {
                      "label": "video.js",
                      "name": "video.js",
                      "fixed": false,
                      "sortable": false,
                      "type": "",
                      "align": "left",
                      "body": []
                    }
                  ],
                  "border": true,
                  "size": "default",
                  "column": 2,
                  "direction": "horizontal"
                }
              ]
            }
          ],
          "header": [
            {
              "renderer": "icon",
              "icon": "Connection",
              "color": "#606266",
              "bgColor": "#ffffff",
              "borderRadius": 0,
              "invert": false,
              "size": 14,
              "classname": ""
            },
            {
              "renderer": "html",
              "html": "&nbsp;第三方依赖库说明",
              "inline": true
            }
          ],
          "actions": [
            {
              "renderer": ""
            }
          ],
          "footer": [
            {
              "renderer": "html",
              "html": "IRenderer基础渲染库共计：72.19kb，编辑器共计：26.48kb",
              "inline": true
            }
          ],
          "classname": "move_2"
        }
      ],
      "bodyStyle": {
        "margin": "20px 0"
      },
      "style": {
        "margin": "20px 0"
      }
    },
    {
      "renderer": "grid",
      "span": 24,
      "gutter": 4,
      "body": [
        {
          "renderer": "service",
          "name": "ChartService",
          "initApi": {
            "url": "/api/mock/chart/map",
            "method": "get",
            "params": {
              "start_time": "${start_time}"
            }
          },
          "body": {
            "renderer": "chart",
            "mapArea": "china",
            "config": {
              "tooltip": {
                "trigger": "item"
              },
              "visualMap": {
                "min": 800,
                "max": 5000,
                "text": [
                  "high",
                  "low"
                ],
                "realtime": false,
                "calculable": true,
                "inRange": {
                  "color": [
                    "#e6f7ff",
                    "#1890FF",
                    "#0050b3"
                  ]
                }
              },
              "series": [
                {
                  "type": "map",
                  "map": "china",
                  "name": "人均收入",
                  "data": "${list}"
                }
              ]
            },
            "height": 500,
            "initData": {
              "start_time": ""
            }
          },
          "classname": "move_3"
        }
      ]
    }
  ],
  "steps": [
    {
      "element": "IWebsiteEdit",
      "popover": {
        "title": "编辑",
        "description": "点击查看或编辑页面"
      }
    },
    {
      "element": "IWebsiteRefresh",
      "popover": {
        "title": "刷新",
        "description": "点击刷新页面"
      }
    },
    {
      "element": "IWebsiteFullScreen",
      "popover": {
        "title": "全屏",
        "description": "点击全屏显示"
      }
    },
    {
      "element": "IWebsiteHome",
      "popover": {
        "title": "官网",
        "description": "点击跳转官网"
      }
    },
    {
      "element": "IWebsiteExpand",
      "popover": {
        "title": "折叠",
        "description": "点击折叠展开菜单"
      }
    }
  ]
}
