pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "table",
      "name": "CouponTasksList",
      "maxHeight": 570,
      "showExport": true,
      "initApi": {
        "url": "/api/mock/coupon/task/list",
        "method": "get"
      },
      "pageConfig": {
        "pageIndex": "pageIndex",
        "pageSize": "pageSize",
        "total": "total",
        "rows": "items"
      },
      "columns": [
        {
          "name": "taskId",
          "label": "任务Id",
          "body": []
        },
        {
          "name": "taskName",
          "label": "任务名称",
          "body": []
        },
        {
          "name": "couponNum",
          "label": "发行量",
          "body": []
        },
        {
          "name": "sendNum",
          "label": "已发行",
          "body": []
        },
        {
          "label": "进度",
          "body": [
            {
              "renderer": "progress",
              "showText": true,
              "exp": "(data.sendNum*100/data.couponNum).toFixed(2)",
              "color": [
                {
                  "color": "#f56c6c",
                  "percentage": 20
                },
                {
                  "color": "#e6a23c",
                  "percentage": 40
                },
                {
                  "color": "#5cb87a",
                  "percentage": 60
                },
                {
                  "color": "#1989fa",
                  "percentage": 80
                },
                {
                  "color": "#6f7ad3",
                  "percentage": 100
                }
              ]
            }
          ]
        },
        {
          "name": "taskStatus",
          "label": "任务状态",
          "body": [
            {
              "renderer": "mapping",
              "value": "<%=data.taskStatus%>",
              "map": {
                "1": "未执行",
                "2": "执行中",
                "3": "执行成功",
                "4": "暂停中",
                "5": "已暂停"
              }
            }
          ]
        },
        {
          "name": "taskStatus",
          "label": "操作",
          "body": [
            {
              "renderer": "action",
              "text": "启动",
              "size": "small",
              "type": "primary",
              "plain": true,
              "actionType": "ajax",
              "actionApi": {
                "url": "/api/mock/tast/start",
                "method": "post",
                "params": "*"
              },
              "target": "LoopService",
              "disabledOn": "data.taskStatus == 2",
              "visibleOn": "![2,3].includes(data.taskStatus)"
            },
            {
              "renderer": "action",
              "text": "执行中",
              "size": "small",
              "type": "primary",
              "plain": true,
              "visibleOn": "data.taskStatus == 2",
              "disabledOn": "data.taskStatus == 2"
            },
            {
              "renderer": "action",
              "text": "暂停",
              "size": "small",
              "type": "primary",
              "plain": true,
              "actionType": "ajax",
              "disabledOn": "data.taskStatus == 4",
              "visibleOn": "data.taskStatus == 2",
              "actionApi": {
                "url": "/api/mock/coupon/task/stop",
                "method": "post",
                "params": "*"
              },
              "target": "LoopService"
            },
            {
              "renderer": "action",
              "text": "暂停中",
              "size": "small",
              "type": "warning",
              "plain": true,
              "actionType": "ajax",
              "disabledOn": "data.taskStatus == 4",
              "visibleOn": "data.taskStatus == 4"
            },
            {
              "renderer": "action",
              "text": "执行成功",
              "size": "small",
              "type": "success",
              "plain": true,
              "visibleOn": "data.taskStatus == 3",
              "disabledOn": "data.taskStatus == 3"
            },
            {
              "renderer": "action",
              "text": "已暂停",
              "size": "small",
              "type": "info",
              "plain": true,
              "visibleOn": "data.taskStatus == 5",
              "disabledOn": "data.taskStatus == 5"
            },
            {
              "url": "http://terra.biz.weibo.com/fansManage/v1/export/coupon/success?taskId=${taskId}",
              "text": "导出成功ID",
              "size": "small",
              "type": "primary",
              "plain": true,
              "visibleOn": "data.taskStatus == 3",
              "blank": true,
              "renderer": "action",
              "actionType": "url"
            },
            {
              "url": "http://terra.biz.weibo.com/fansManage/v1/export/coupon/original?taskId=${taskId}",
              "text": "导出上传ID",
              "size": "small",
              "type": "primary",
              "plain": true,
              "visibleOn": "data.taskStatus == 3",
              "blank": true,
              "renderer": "action",
              "actionType": "url"
            },
            {
              "renderer": "action",
              "size": "small",
              "plain": true,
              "text": "任务详情",
              "visibleOn": "true",
              "actionType": "dialog",
              "body": {
                "title": "任务详情",
                "body": {
                  "renderer": "static",
                  "column": 2,
                  "controls": [
                    {
                      "name": "taskName",
                      "label": "任务名称"
                    },
                    {
                      "name": "taskReason",
                      "label": "任务描述"
                    },
                    {
                      "name": "couponNum",
                      "label": "后台记录发行量",
                      "width": "100"
                    },
                    {
                      "name": "sendNum",
                      "label": "后台记录已发行量",
                      "width": "100"
                    },
                    {
                      "name": "circulation",
                      "label": "优惠券系统记录发行量",
                      "width": "100"
                    },
                    {
                      "name": "receiveNum",
                      "label": "优惠券系统记录已发行量",
                      "width": "100"
                    },
                    {
                      "name": "usageNum",
                      "label": "已使用券数量",
                      "width": "100"
                    },
                    {
                      "name": "title",
                      "label": "标题",
                      "width": "100"
                    },
                    {
                      "name": "type",
                      "label": "优惠券类型",
                      "body": {
                        "renderer": "mapping",
                        "value": "<%=data.type%>",
                        "map": {
                          "1": "满减",
                          "2": "折扣",
                          "3": "代金券"
                        }
                      }
                    },
                    {
                      "name": "discountRate",
                      "label": "折扣率(非折扣券默认1)",
                      "width": "100"
                    },
                    {
                      "name": "fullPrice",
                      "label": "满减券的满额(元)",
                      "width": "100"
                    },
                    {
                      "name": "discountPrice",
                      "label": "优惠金额(元)",
                      "width": "100"
                    },
                    {
                      "name": "scope",
                      "label": "券使用范围",
                      "body": {
                        "renderer": "mapping",
                        "value": "<%=data.scope%>",
                        "initApi": {
                          "url": "/fansManage/v1/coupon/task/scope",
                          "method": "get",
                          "cached": true
                        },
                        "silentMessage": true
                      }
                    },
                    {
                      "name": "useRule",
                      "label": "券使用规则",
                      "width": "100"
                    },
                    {
                      "name": "endValidTime",
                      "label": "券码有效期结束时间",
                      "width": "100"
                    },
                    {
                      "name": "beginValidTime",
                      "label": "券码有效期开始时间",
                      "width": "100"
                    },
                    {
                      "name": "endTime",
                      "label": "券模版有效期结束时间",
                      "width": "100"
                    },
                    {
                      "name": "beginTime",
                      "label": "券模版有效期开始时间",
                      "width": "100"
                    },
                    {
                      "name": "createTime",
                      "label": "创建券模版时间",
                      "width": "100"
                    },
                    {
                      "name": "updateTime",
                      "label": "券模版更新时间",
                      "width": "100"
                    },
                    {
                      "name": "operator",
                      "label": "任务操作人",
                      "width": "100"
                    },
                    {
                      "name": "taskStatus",
                      "label": "任务状态",
                      "width": "100",
                      "body": {
                        "renderer": "mapping",
                        "value": "<%=data.taskStatus%>",
                        "map": {
                          "1": "未执行",
                          "2": "执行中",
                          "3": "执行成功",
                          "4": "暂停中",
                          "5": "已暂停"
                        }
                      }
                    },
                    {
                      "name": "extend",
                      "label": "扩展字段",
                      "width": "100"
                    },
                    {
                      "name": "dateType",
                      "label": "优惠券有效期类型",
                      "width": "100",
                      "body": {
                        "renderer": "mapping",
                        "value": "<%=data.dateType%>",
                        "map": {
                          "0": "自然天",
                          "1": "精确到秒"
                        }
                      }
                    }
                  ],
                  "columns": [
                    {
                      "label": "优惠券名称",
                      "name": "taskName",
                      "align": "",
                      "body": []
                    },
                    {
                      "label": "已下发",
                      "name": "sendNum",
                      "align": "",
                      "body": []
                    },
                    {
                      "label": "优惠券总数",
                      "name": "couponNum",
                      "align": "",
                      "body": []
                    }
                  ],
                  "border": true
                },
                "fullscreen": false,
                "center": false,
                "draggable": false,
                "closeOnClickModal": true,
                "closeOnPressEscape": true
              },
              "actionApi": {
                "url": "",
                "method": "post",
                "params": {}
              },
              "params": {}
            },
            {
              "renderer": "service",
              "name": "LoopService",
              "silentMessage": true,
              "silentLoading": true,
              "initApi": {
                "url": "/api/mock/bp/state",
                "method": "get",
                "params": "*"
              },
              "linkedOn": "data.taskId == target.taskId",
              "sendOn": "[2,4,'2','4'].includes(data.taskStatus)",
              "reload": "CouponTasksList",
              "stopAutoRefreshWhen": "[3,\"3\",5,\"5\"].includes(data.taskStatus)",
              "interval": 3000,
              "timeout": 200000
            }
          ]
        }
      ],
      "actions": [],
      "hasPageInfo": false,
      "reactive": true,
      "highlightCurrentRow": false,
      "rowKey": "taskId"
    }
  ]
}
