pageSchema:
{
  "renderer": "page",
  "body": [
    {
      "renderer": "table",
      "columns": [
        {
          "label": "id",
          "name": "id",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none"
        },
        {
          "label": "文案",
          "name": "text",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none"
        },
        {
          "label": "进度条",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "width": 200,
          "body": [
            {
              "renderer": "progress",
              "name": "progress",
              "exp": "data.progress",
              "status": "success",
              "color": []
            }
          ],
          "name": "progress"
        },
        {
          "label": "字典",
          "name": "type",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [
            {
              "renderer": "mapping",
              "name": "type",
              "status": "type",
              "optionConfig": {},
              "map": {
                "1": "第一个",
                "2": "第二个",
                "3": "第三个",
                "4": "第四个",
                "5": "第五个"
              }
            }
          ]
        },
        {
          "label": "时间",
          "name": "date",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none"
        },
        {
          "label": "音频",
          "name": "audio",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "width": 330,
          "body": [
            {
              "renderer": "audio",
              "src": "${audio}",
              "preload": "none"
            }
          ]
        },
        {
          "label": "图片",
          "name": "image",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "width": 100,
          "body": [
            {
              "renderer": "image",
              "src": "${image}",
              "previews": []
            }
          ]
        },
        {
          "label": "状态",
          "name": "boolean",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none"
        },
        {
          "label": "列表",
          "name": "list",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "width": 200,
          "body": [
            {
              "renderer": "each",
              "body": [
                {
                  "renderer": "html",
                  "html": "<h1><%=data.title%></h1>",
                  "inline": true
                }
              ],
              "name": "list"
            }
          ]
        },
        {
          "label": "轮播",
          "name": "carousel",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "width": 200,
          "body": [
            {
              "renderer": "wrapper",
              "inherit": {
                "type": "include",
                "value": [
                  "carousel"
                ]
              },
              "body": [
                {
                  "renderer": "carousel",
                  "name": "carousel",
                  "body": [
                    {
                      "renderer": "image",
                      "src": "${image1}"
                    },
                    {
                      "renderer": "html",
                      "html": "<%=data.html%>"
                    },
                    {
                      "renderer": "image",
                      "src": "${image2}"
                    }
                  ],
                  "autoplay": false,
                  "height": 100
                }
              ],
              "bodyClassname": "big-table-carousel"
            }
          ]
        },
        {
          "label": "视频",
          "name": "text",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "width": 300,
          "body": [
            {
              "renderer": "video",
              "src": "${video}",
              "poster": "${poster}",
              "height": 120,
              "width": 260
            }
          ]
        },
        {
          "label": "操作",
          "name": "text",
          "sortable": false,
          "fixed": false,
          "type": "-",
          "align": "none",
          "body": [
            {
              "renderer": "action",
              "text": "数据",
              "actionType": "dialog",
              "body": {
                "body": [
                  {
                    "renderer": "data"
                  }
                ]
              },
              "isText": true
            }
          ]
        }
      ],
      "actions": [],
      "promiseType": "single",
      "initApi": {
        "url": "/api/mock/bigTable",
        "method": "get",
        "cached": false,
        "params": {}
      }
    }
  ],
  "innerStyle": ".big-table-carousel {\n  .el-image {\n    height: 100px;\n  }\n}"
}
