pageSchema:
{
  "renderer": "page",
  "initData": {
    "map": {
      "2222": "www",
      "www": "111"
    },
    "name": {
      "aaa": "1234"
    },
    "tabs": [],
    "conditions": []
  },
  "body": [
    {
      "renderer": "form",
      "controls": [
        {
          "renderer": "divider",
          "text": "简单组合示例"
        },
        {
          "renderer": "combo",
          "name": "array",
          "label": "简单数组",
          "requiredOn": "1",
          "inline": false,
          "type": "array"
        },
        {
          "renderer": "combo",
          "name": "map",
          "label": "简单字典",
          "requiredOn": "1",
          "inline": false,
          "type": "map"
        },
        {
          "renderer": "viewer",
          "body": [
            {
              "renderer": "data"
            }
          ],
          "target": ".i-main__container"
        },
        {
          "renderer": "divider",
          "text": "复合组合示例"
        },
        {
          "renderer": "switch",
          "name": "collapse",
          "inlinePrompt": false,
          "type": "button",
          "activeText": "展开",
          "inactiveText": "收起",
          "rules": []
        },
        {
          "renderer": "combo",
          "name": "multiplle",
          "label": "复合数组",
          "requiredOn": 1,
          "inline": false,
          "type": "multiple",
          "controls": [
            {
              "renderer": "radio",
              "label": "radio",
              "name": "radio",
              "requiredOn": "1"
            },
            {
              "renderer": "input",
              "label": "input",
              "name": "input",
              "requiredOn": "1"
            }
          ]
        },
        {
          "renderer": "combo",
          "name": "tabs",
          "label": "选项卡",
          "requiredOn": 1,
          "isTab": true,
          "controls": [
            {
              "renderer": "radio",
              "label": "radio",
              "name": "radio",
              "requiredOn": "1"
            },
            {
              "renderer": "input",
              "label": "input",
              "name": "input",
              "requiredOn": "1"
            }
          ],
          "category": "table"
        },
        {
          "renderer": "combo",
          "name": "custom",
          "label": "复合字典",
          "requiredOn": "1",
          "inline": false,
          "type": "custom",
          "controls": [
            {
              "renderer": "input",
              "label": "aaa",
              "name": "aaa",
              "requiredOn": "1"
            },
            {
              "renderer": "input",
              "label": "bbb",
              "name": "bbb",
              "requiredOn": "1"
            }
          ]
        },
        {
          "renderer": "viewer",
          "body": [
            {
              "renderer": "html",
              "html": "组合与轮播联动",
              "classname": "text-center strong"
            },
            {
              "renderer": "carousel",
              "name": "tabs",
              "autoplay": true,
              "body": [
                {
                  "renderer": "data"
                }
              ]
            },
            {
              "renderer": "timeline",
              "body": [
                {
                  "renderer": "alert",
                  "type": "${input}"
                }
              ],
              "name": "tabs",
              "type": "input",
              "placement": "top",
              "hollow": true
            }
          ],
          "computedClass": "$.collapse? 'viewer__collapse': ''",
          "target": ".i-main__container"
        },
        {
          "renderer": "divider",
          "text": "复杂嵌套"
        },
        {
          "renderer": "combo",
          "name": "conditions",
          "label": "检索条件",
          "border": false,
          "inline": true,
          "controls": [
            {
              "renderer": "select",
              "name": "domain",
              "label": "",
              "options": [
                {
                  "text": "就诊信息表",
                  "value": "1"
                },
                {
                  "text": "诊断信息表",
                  "value": "2",
                  "disabledOn": "$.comboIndex == 0"
                }
              ]
            },
            {
              "renderer": "select",
              "name": "type",
              "label": "",
              "placeholder": "入院信息表",
              "visibleOn": "$.domain == 1",
              "options": [
                {
                  "text": "年龄",
                  "value": "1"
                },
                {
                  "text": "性别",
                  "value": "2"
                }
              ]
            },
            {
              "renderer": "radio",
              "name": "sex",
              "label": "",
              "placeholder": "性别",
              "visibleOn": "$.domain == 1 && $.type == 2",
              "options": [
                {
                  "text": "男",
                  "value": "1"
                },
                {
                  "text": "女",
                  "value": "2"
                }
              ]
            },
            {
              "renderer": "select",
              "name": "ageType",
              "label": "",
              "placeholder": "年龄",
              "visibleOn": "$.domain == 1 && $.type == 1",
              "options": [
                {
                  "text": "范围",
                  "value": "1"
                },
                {
                  "text": "具体",
                  "value": "2"
                }
              ]
            },
            {
              "renderer": "input",
              "label": "",
              "placeholder": "最小年龄",
              "name": "minAge",
              "visibleOn": "$.domain == 1 && $.type == 1 && $.ageType == 1"
            },
            {
              "renderer": "input",
              "label": "",
              "placeholder": "最大年龄",
              "name": "maxAge",
              "visibleOn": "$.domain == 1 && $.type == 1 && $.ageType == 1"
            },
            {
              "renderer": "input",
              "label": "",
              "placeholder": "年龄",
              "name": "age",
              "visibleOn": "$.domain == 1 && $.type == 1 && $.ageType == 2"
            },
            {
              "renderer": "select",
              "name": "type",
              "label": "",
              "visibleOn": "$.domain == 2",
              "options": [
                {
                  "text": "呼吸科",
                  "value": "1"
                },
                {
                  "text": "外科",
                  "value": "2"
                },
                {
                  "text": "肿瘤科",
                  "value": "3"
                }
              ]
            },
            {
              "renderer": "select",
              "name": "relation",
              "placeholder": "请选择关系",
              "requiredOn": "1",
              "options": [
                {
                  "text": "或",
                  "value": "1"
                },
                {
                  "text": "且",
                  "value": "2"
                }
              ]
            },
            {
              "renderer": "combo",
              "name": "children",
              "visibleOn": "$.relation == 2",
              "label": "",
              "inline": true,
              "type": "custom",
              "border": false,
              "controls": [
                {
                  "renderer": "select",
                  "name": "domain",
                  "label": "",
                  "options": [
                    {
                      "text": "就诊信息表",
                      "value": "1"
                    },
                    {
                      "text": "诊断信息表",
                      "value": "2"
                    }
                  ]
                },
                {
                  "renderer": "select",
                  "name": "type",
                  "label": "",
                  "placeholder": "入院信息表",
                  "visibleOn": "$.domain == 1",
                  "options": [
                    {
                      "text": "年龄",
                      "value": "1"
                    },
                    {
                      "text": "性别",
                      "value": "2"
                    }
                  ]
                },
                {
                  "renderer": "radio",
                  "name": "sex",
                  "label": "",
                  "placeholder": "性别",
                  "visibleOn": "$.domain == 1 && $.type == 2",
                  "options": [
                    {
                      "text": "男",
                      "value": "1"
                    },
                    {
                      "text": "女",
                      "value": "2"
                    }
                  ]
                },
                {
                  "renderer": "select",
                  "name": "ageType",
                  "label": "",
                  "placeholder": "年龄",
                  "visibleOn": "$.domain == 1 && $.type == 1",
                  "options": [
                    {
                      "text": "范围",
                      "value": "1"
                    },
                    {
                      "text": "具体",
                      "value": "2"
                    }
                  ]
                },
                {
                  "renderer": "input",
                  "label": "",
                  "placeholder": "最小年龄",
                  "name": "minAge",
                  "visibleOn": "$.domain == 1 && $.type == 1 && $.ageType == 1"
                },
                {
                  "renderer": "input",
                  "label": "",
                  "placeholder": "最大年龄",
                  "name": "maxAge",
                  "visibleOn": "$.domain == 1 && $.type == 1 && $.ageType == 1"
                },
                {
                  "renderer": "input",
                  "label": "",
                  "placeholder": "年龄",
                  "name": "age",
                  "visibleOn": "$.domain == 1 && $.type == 1 && $.ageType == 2"
                },
                {
                  "renderer": "select",
                  "name": "type",
                  "label": "",
                  "visibleOn": "$.domain == 2",
                  "options": [
                    {
                      "text": "呼吸科",
                      "value": "1"
                    },
                    {
                      "text": "外科",
                      "value": "2"
                    },
                    {
                      "text": "肿瘤科",
                      "value": "3"
                    }
                  ]
                }
              ]
            }
          ],
          "extends": true
        },
        {
          "renderer": "action",
          "actionType": "submit"
        },
        {
          "renderer": "html"
        }
      ],
      "classname": "actions-fixed"
    }
  ],
  "innerStyle": ".viewer__collapse {\n  width: 40px !important;\n  padding-left: 20px;\n  white-space: nowrap;\n}\n.el-table__header,\n.el-table__body {\n  width: unset !important;\n}"
}
